import { supabase } from './supabase';
import { Alert } from 'react-native';
import NotificationService from './NotificationService';

interface Friend {
    user2Id: string;
    username: string;
    points: number;
}

export const removeFriend = async (
    userId: string,
    friendId: string
): Promise<boolean> => {
    try {
        console.log('Iniciando eliminación de amigo:', { userId, friendId });

        // Obtener el nombre del amigo que se va a eliminar
        const { data: friendData, error: friendError } = await supabase
            .from('users')
            .select('username')
            .eq('id', friendId)
            .single();

        if (friendError) {
            console.error('Error al obtener datos del amigo:', friendError);
            throw friendError;
        }

        console.log('Datos del amigo obtenidos:', friendData);

        // Eliminar solo una relación (el trigger se encargará de la recíproca)
        const { error } = await supabase
            .from('friends')
            .delete()
            .eq('user1Id', userId)
            .eq('user2Id', friendId);

        if (error) {
            console.error('Error al eliminar la relación de amistad:', error);
            throw error;
        }

        console.log('Relación de amistad eliminada correctamente');

        // Enviar notificación al amigo eliminado
        const notificationService = NotificationService.getInstance();
        await notificationService.createNotification(
            friendId,
            'Amistad eliminada',
            `Has sido eliminado/a de la lista de amigos`,
            'friend_removed'
        );

        console.log('Notificación enviada al amigo eliminado');
        Alert.alert('Éxito', `Amigo eliminado correctamente`);
        return true;
    } catch (err) {
        console.error('Error completo al eliminar amigo:', err);
        Alert.alert('Error', 'No se pudo eliminar el amigo');
        return false;
    }
};
