const MapScreen = () => {
  const navigation = useNavigation<NavigationProp>();
  const dispatch = useDispatch();
  const { user } = useSelector((state: RootState) => state.auth);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState<City[]>([]);
  const [showSearchResults, setShowSearchResults] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showCalendar, setShowCalendar] = useState(false);
  const [selectedCity, setSelectedCity] = useState<City | null>(null);
  const [showGlobe, setShowGlobe] = useState(false);

  const formatDate = (date: Date) => {
    return date.toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleSearch = async (query: string) => {
    if (query.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      setLoading(true);
      const results = await searchCities(query);
      setSearchResults(results);
      setShowSearchResults(true);
    } catch (error) {
      console.error('Error searching cities:', error);
      Alert.alert('Error', 'No se pudieron cargar los resultados de búsqueda');
    } finally {
      setLoading(false);
    }
  };

  const handleCitySelect = (city: City) => {
    setSelectedCity(city);
    setSearchQuery(city.name);
    setShowSearchResults(false);
  };

  const handleDateSelect = (date: string) => {
    setSelectedDate(new Date(date));
    setShowCalendar(false);
  };

  const handleGenerateMission = async () => {
    if (!selectedCity) {
      Alert.alert('Error', 'Por favor selecciona una ciudad');
      return;
    }

    try {
      setLoading(true);
      const mission = await generateMission(selectedCity, selectedDate);
      navigation.navigate('Missions', {
        journeyId: mission.id,
        challenges: mission.challenges
      });
    } catch (error) {
      console.error('Error generating mission:', error);
      Alert.alert('Error', 'No se pudo generar la misión');
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.searchContainer}>
        <TextInput
          style={styles.searchInput}
          placeholder="Buscar ciudad..."
          value={searchQuery}
          onChangeText={(text) => {
            setSearchQuery(text);
            handleSearch(text);
          }}
        />
        <TouchableOpacity
          style={styles.dateButton}
          onPress={() => setShowCalendar(true)}
        >
          <Text style={styles.dateButtonText}>
            {formatDate(selectedDate)}
          </Text>
        </TouchableOpacity>
      </View>

      {showSearchResults && (
        <View style={styles.searchResults}>
          <FlatList
            data={searchResults}
            keyExtractor={(item) => item.id}
            renderItem={({ item }) => (
              <TouchableOpacity
                style={styles.searchItem}
                onPress={() => handleCitySelect(item)}
              >
                <Text>{item.name}</Text>
              </TouchableOpacity>
            )}
          />
        </View>
      )}

      <Modal
        visible={showCalendar}
        animationType="slide"
        transparent={true}
      >
        <View style={styles.modalContainer}>
          <View style={styles.calendarContainer}>
            <Calendar
              onDayPress={(day) => handleDateSelect(day.dateString)}
              markedDates={{
                [selectedDate.toISOString().split('T')[0]]: {
                  selected: true,
                  selectedColor: '#4CAF50'
                }
              }}
            />
            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => setShowCalendar(false)}
            >
              <Text>Cerrar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>

      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#4CAF50" />
        </View>
      ) : (
        <View style={styles.mapContainer}>
          {showGlobe ? (
            <GlobeView />
          ) : (
            <Map
              selectedCity={selectedCity}
              onCitySelect={setSelectedCity}
            />
          )}
        </View>
      )}

      <View style={styles.buttonContainer}>
        <TouchableOpacity
          style={styles.generateButton}
          onPress={handleGenerateMission}
          disabled={!selectedCity || loading}
        >
          <Text style={styles.generateButtonText}>
            {loading ? 'Generando...' : 'Generar Misión'}
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.viewToggleButton}
          onPress={() => setShowGlobe(!showGlobe)}
        >
          <Ionicons
            name={showGlobe ? 'map' : 'globe'}
            size={24}
            color="#4CAF50"
          />
        </TouchableOpacity>
      </View>
    </View>
  );
};

