import { supabase } from './supabase';

export const getUserPoints = async (userId: string) => {
    try {
        const { data, error } = await supabase
            .from('users')
            .select('points')
            .eq('id', userId)
            .maybeSingle();

        if (error) throw error;

        return data?.points || 0;
    } catch (error) {
        console.error('Error obteniendo puntos del usuario:', error);
        return 0; // Retornamos 0 en caso de error
    }
};

export const addPointsToUser = async (userId: string, points: number) => {
    try {
        // Primero obtenemos los puntos actuales
        const currentPoints = await getUserPoints(userId);

        // Actualizamos los puntos
        const { error } = await supabase
            .from('users')
            .update({
                points: currentPoints + points,
                updated_at: new Date().toISOString()
            })
            .eq('id', userId);

        if (error) throw error;

        return currentPoints + points;
    } catch (error) {
        console.error('Error añadiendo puntos al usuario:', error);
        throw error;
    }
};

export const completeMission = async (missionId: string, userId: string, imageUrl?: string) => {
    try {
        // Primero obtenemos los datos de la misión
        const { data: missionData, error: missionError } = await supabase
            .from('journeys_missions')
            .select(`
                id,
                journeyId,
                challenge:challenges (
                    id,
                    title,
                    description,
                    points
                )
            `)
            .eq('id', missionId)
            .single();

        if (missionError) throw missionError;

        // Marcar la misión como completada
        try {
            const { error: updateError } = await supabase
                .from('journeys_missions')
                .update({ 
                    completed: true,
                    completed_at: new Date().toISOString(),
                    photo_url: imageUrl || null
                })
                .eq('id', missionId);

            if (updateError) {
                // Si el error está relacionado con photo_url, intentamos de nuevo sin esa columna
                if (updateError.message && 
                    (updateError.message.includes('photo_url') || 
                     updateError.message.includes('column') || 
                     updateError.code === 'PGRST204')) {
                    
                    console.warn('La columna photo_url no existe en journeys_missions, actualizando sin la foto');
                    
                    const { error: retryError } = await supabase
                        .from('journeys_missions')
                        .update({
                            completed: true,
                            completed_at: new Date().toISOString()
                        })
                        .eq('id', missionId);
                    
                    if (retryError) throw retryError;
                } else {
                    throw updateError;
                }
            }
        } catch (error: any) {
            // Si seguimos teniendo un error relacionado con photo_url después del reintento,
            // lo registramos pero continuamos con el proceso
            if (error.message && 
                (error.message.includes('photo_url') || 
                 error.message.includes('column') || 
                 error.code === 'PGRST204')) {
                console.warn('Error con la columna photo_url pero continuando:', error);
            } else {
                throw error;
            }
        }

        // Añadir los puntos al usuario
        const points = missionData.challenge.points;
        await addPointsToUser(userId, points);

        // Si hay una imagen, intentamos agregar una entrada al diario
        if (imageUrl) {
            try {
                // Obtener información de la ciudad
                const { data: journeyData, error: journeyError } = await supabase
                    .from('journeys')
                    .select(`
                        id,
                        cityId,
                        cities (name)
                    `)
                    .eq('id', missionData.journeyId)
                    .single();

                if (journeyError) {
                    console.warn('Error obteniendo datos de journey:', journeyError);
                    return points; // Retornamos puntos y no creamos entrada en el diario
                }

                // Intentar crear entrada en el diario
                try {
                    const { error: journalError } = await supabase
                        .from('journal_entries')
                        .insert({
                            userId,
                            cityId: journeyData.cityId,
                            missionId,
                            title: missionData.challenge.title,
                            content: `Misión completada: ${missionData.challenge.description}`,
                            photos: [imageUrl],
                            location: null,
                            created_at: new Date().toISOString(),
                            tags: ['misión', journeyData.cities.name.toLowerCase()]
                        });

                    if (journalError) {
                        console.warn('Error al crear entrada en el diario, pero la misión ha sido completada:', journalError);
                    }
                } catch (journalErr) {
                    console.warn('Error al intentar crear entrada en el diario, pero la misión ha sido completada:', journalErr);
                }
            } catch (err) {
                console.warn('Error en el proceso de creación del diario, pero la misión ha sido completada:', err);
            }
        }

        return points;
    } catch (error) {
        console.error('Error completando la misión:', error);
        throw error;
    }
};
