import React, { useEffect, useState } from 'react';
import { View, Text, Button, StyleSheet, FlatList, TouchableOpacity, RefreshControl, Alert } from 'react-native';
import NotificationService from '../services/NotificationService';
import { useAppSelector } from '../features/hooks';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';
import { Notification, PushToken } from '../types/notifications';
import { Ionicons } from '@expo/vector-icons';

export const NotificationTest = () => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [loading, setLoading] = useState(true);
    const [refreshing, setRefreshing] = useState(false);
    const [pushToken, setPushToken] = useState<PushToken | null>(null);
    const userId = useAppSelector(state => state.auth.user?.id);
    const notificationService = NotificationService.getInstance();

    useEffect(() => {
        if (userId) {
            loadNotifications();
        }
    }, [userId]);

    const loadNotifications = async () => {
        try {
            setLoading(true);
            const unreadNotifications = await NotificationService.getUnreadNotifications(userId!);
            setNotifications(unreadNotifications);
        } catch (error) {
            console.error('Error loading notifications:', error);
            Alert.alert('Error', 'No se pudieron cargar las notificaciones');
        } finally {
            setLoading(false);
            setRefreshing(false);
        }
    };

    const handleNotificationPress = async (notification: Notification) => {
        try {
            await NotificationService.markNotificationAsRead(notification.id);
            loadNotifications(); // Recargar las notificaciones
        } catch (error) {
            console.error('Error marking notification as read:', error);
            Alert.alert('Error', 'No se pudo marcar la notificación como leída');
        }
    };

    const testLocalNotification = async () => {
        try {
            // Programar una notificación para dentro de 5 segundos
            await notificationService.scheduleLocalNotification(
                '¡Prueba de notificación!',
                'Esta es una notificación local de prueba',
                { seconds: 5 }
            );
            console.log('Notificación local programada correctamente');
        } catch (error) {
            console.error('Error al programar notificación local:', error);
        }
    };

    const testDatabaseNotification = async () => {
        if (!userId) {
            console.log('No hay usuario autenticado');
            Alert.alert('Error', 'No hay usuario autenticado');
            return;
        }

        try {
            await NotificationService.createNotification(
                userId,
                'Prueba de Notificación en Base de Datos',
                'Esta es una notificación guardada en la base de datos',
                'test',
                { test: true }
            );
            console.log('Notificación guardada en la base de datos');
            Alert.alert('Éxito', 'Notificación guardada en la base de datos');
            loadNotifications(); // Recargar las notificaciones
        } catch (error) {
            console.error('Error al crear notificación en base de datos:', error);
            Alert.alert('Error', 'No se pudo guardar la notificación en la base de datos');
        }
    };

    const testPushNotification = async () => {
        try {
            setLoading(true);
            const token = await NotificationService.registerForPushNotificationsAsync();
            if (token) {
                setPushToken(token as PushToken);
                console.log('Token de notificación push:', token);
                Alert.alert('Éxito', 'Dispositivo registrado para notificaciones push');
            }
        } catch (error) {
            console.error('Error al registrar notificaciones push:', error);
            Alert.alert('Error', 'No se pudo registrar el dispositivo para notificaciones push');
        } finally {
            setLoading(false);
        }
    };

    const renderNotification = ({ item }: { item: Notification }) => (
        <TouchableOpacity
            style={styles.notificationItem}
            onPress={() => handleNotificationPress(item)}
        >
            <View style={styles.notificationContent}>
                <View style={styles.notificationHeader}>
                    <Text style={styles.notificationTitle}>{item.title}</Text>
                    <Ionicons name="chevron-forward" size={20} color="#666" />
                </View>
                <Text style={styles.notificationMessage}>{item.message}</Text>
                <Text style={styles.notificationDate}>
                    {format(new Date(item.created_at), 'PPP p', { locale: es })}
                </Text>
            </View>
        </TouchableOpacity>
    );

    return (
        <View style={styles.container}>
            <View style={styles.testButtonsContainer}>
                <Text style={styles.sectionTitle}>Prueba de Notificaciones</Text>

                {pushToken && (
                    <View style={styles.tokenContainer}>
                        <Text style={styles.tokenLabel}>Token Push:</Text>
                        <Text style={styles.tokenValue}>{pushToken.token}</Text>
                    </View>
                )}

                <View style={styles.buttonContainer}>
                    <Button
                        title="Probar Notificación Local"
                        onPress={testLocalNotification}
                    />
                </View>

                <View style={styles.buttonContainer}>
                    <Button
                        title="Probar Notificación en Base de Datos"
                        onPress={testDatabaseNotification}
                    />
                </View>

                <View style={styles.buttonContainer}>
                    <Button
                        title="Registrar para Notificaciones Push"
                        onPress={testPushNotification}
                    />
                </View>
            </View>

            <View style={styles.notificationsContainer}>
                <Text style={styles.sectionTitle}>Notificaciones</Text>
                {loading ? (
                    <View style={styles.loadingContainer}>
                        <Text>Cargando notificaciones...</Text>
                    </View>
                ) : (
                    <FlatList
                        data={notifications}
                        renderItem={renderNotification}
                        keyExtractor={item => item.id}
                        refreshControl={
                            <RefreshControl
                                refreshing={refreshing}
                                onRefresh={() => {
                                    setRefreshing(true);
                                    loadNotifications();
                                }}
                            />
                        }
                        ListEmptyComponent={
                            <View style={styles.emptyContainer}>
                                <Text>No hay notificaciones nuevas</Text>
                            </View>
                        }
                    />
                )}
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
    },
    testButtonsContainer: {
        marginBottom: 20,
    },
    notificationsContainer: {
        flex: 1,
    },
    sectionTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 15,
        textAlign: 'center',
    },
    buttonContainer: {
        marginVertical: 8,
    },
    notificationItem: {
        backgroundColor: '#fff',
        borderRadius: 8,
        padding: 16,
        marginBottom: 8,
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.1,
        shadowRadius: 3,
        elevation: 2,
    },
    notificationContent: {
        flex: 1,
    },
    notificationHeader: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 4,
    },
    notificationTitle: {
        fontSize: 16,
        fontWeight: 'bold',
        flex: 1,
    },
    notificationMessage: {
        fontSize: 14,
        color: '#666',
        marginBottom: 8,
    },
    notificationDate: {
        fontSize: 12,
        color: '#999',
    },
    emptyContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
    },
    loadingContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
    },
    tokenContainer: {
        backgroundColor: '#f5f5f5',
        padding: 10,
        borderRadius: 5,
        marginBottom: 15,
    },
    tokenLabel: {
        fontSize: 14,
        fontWeight: 'bold',
        marginBottom: 5,
    },
    tokenValue: {
        fontSize: 12,
        color: '#666',
        fontFamily: 'monospace',
    },
});
