import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, ActivityIndicator } from 'react-native';
import { supabase } from '../../services/supabase';
import { LinearGradient } from 'expo-linear-gradient';
import { useSelector } from 'react-redux';
import { RootState } from '../../features/store';

const colors = {
  primary: '#005F9E',
  secondary: '#FFFFFF',
  danger: '#D32F2F',
  backgroundGradient: ['#005F9E', '#F0F0F0'],
};

const MissionsScreen = () => {  
  const [journeyMissions, setJourneyMissions] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [updatingMission, setUpdatingMission] = useState<number | null>(null);
  const { user } = useSelector((state: RootState) => state.auth);
  const [username, setUsername] = useState('');

  useEffect(() => {
    const fetchUsername = async () => {
      if (!user?.id) return;
      try {
        const { data, error } = await supabase
          .from('users')
          .select('username')
          .eq('id', user.id)
          .single();
        if (error) throw error;
        setUsername(data.username);
      } catch (err: any) {
        console.error('Error al obtener el username:', err.message);
      }
    };
    fetchUsername();
  }, [user]);

  useEffect(() => {
    const fetchUserMissions = async () => {
      if (!user?.id) return;
      try {
        const { data, error } = await supabase
          .from('journeys_missions')
          .select('*')
          .eq('userId', user.id);
        if (error) throw error;
        setJourneyMissions(data || []);
      } catch (err: any) {
        console.error('Error al obtener las misiones:', err.message);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchUserMissions();
  }, [user]);

  const handleCompleteMission = async (missionId: number) => {
    try {
      setUpdatingMission(missionId);
      const { error } = await supabase
        .from('journeys_missions')
        .update({ completed: true })
        .eq('id', missionId);
      if (error) throw error;
      setJourneyMissions(journeyMissions.map(mission => 
        mission.id === missionId 
          ? { ...mission, completed: true }
          : mission
      ));
    } catch (err: any) {
      console.error('Error al completar la misión:', err.message);
    } finally {
      setUpdatingMission(null);
    }
  };

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={colors.primary} />
        <Text style={styles.text}>Cargando misiones...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.errorContainer}>
        <Text style={styles.errorText}>Error: {error}</Text>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <LinearGradient colors={colors.backgroundGradient} style={styles.header}>
        <Text style={styles.headerTitle}>Misiones de Viaje</Text>
        <Text style={styles.headerSubtitle}>¡Completa tus desafíos, {username}!</Text>
      </LinearGradient>

      {journeyMissions.map((mission) => (
        <View key={mission.id} style={styles.missionCard}>
          <Text style={styles.missionTitle}>{mission.challenges?.title || 'Sin título'}</Text>
          <Text style={styles.difficultyLevel}>Nivel: {mission.challenges?.difficulty || 'No especificado'}</Text>
          <Text style={[styles.missionStatus, mission.completed ? styles.completed : styles.pending]}>
            {mission.completed ? '✓ Completada' : '⏳ Pendiente'}
          </Text>
          {!mission.completed && (
            <TouchableOpacity 
              style={styles.completeButton}
              onPress={() => handleCompleteMission(mission.id)}
              disabled={updatingMission === mission.id}
            >
              {updatingMission === mission.id ? (
                <ActivityIndicator size="small" color={colors.secondary} />
              ) : (
                <Text style={styles.completeButtonText}>Completar Misión</Text>
              )}
            </TouchableOpacity>
          )}
        </View>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.backgroundGradient[1],
  },
  header: {
    padding: 24,
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
    marginBottom: 20,
  },
  headerTitle: {
    fontSize: 26,
    fontWeight: 'bold',
    color: colors.secondary,
    textAlign: 'center',
  },
  headerSubtitle: {
    fontSize: 18,
    color: colors.secondary,
    textAlign: 'center',
  },
  missionCard: {
    backgroundColor: colors.secondary,
    padding: 15,
    margin: 10,
    borderRadius: 10,
    elevation: 4,
  },
  missionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: colors.primary,
  },
  difficultyLevel: {
    fontSize: 16,
    color: '#FFA000',
    marginVertical: 5,
  },
  missionStatus: {
    fontSize: 16,
    marginBottom: 10,
    fontWeight: '600',
  },
  completed: {
    color: '#2E7D32',
  },
  pending: {
    color: colors.danger,
  },
  completeButton: {
    backgroundColor: colors.primary,
    padding: 12,
    borderRadius: 8,
    alignItems: 'center',
  },
  completeButtonText: {
    color: colors.secondary,
    fontSize: 16,
    fontWeight: 'bold',
  },
  errorText: {
    color: colors.danger,
    textAlign: 'center',
    padding: 16,
  },
  text: {
    fontSize: 16,
    color: colors.primary,
    textAlign: 'center',
  },
});

export default MissionsScreen;
