        } catch(e) {
          console.warn('Error detectando rendimiento del dispositivo:', e);
          isLowPerformanceDevice = true; // Asumir bajo rendimiento por seguridad
        }
        
        // Actualizar el progreso de carga
        function updateProgress(text) {
