                    document.getElementById('loadingOverlay').style.display = 'none';
                  }
                }, 1000);
              } catch (e) {
                console.error('Error al configurar vista inicial:', e);
                // Informar que se completó de todos modos
                window.ReactNativeWebView.postMessage('loaded');
                document.getElementById('loadingOverlay').style.display = 'none';
