            viewer.scene.globe.enableLighting = false;
            viewer.scene.fog.enabled = false;
            viewer.scene.globe.showGroundAtmosphere = false;
            
            // Forzar modo 3D estricto
            viewer.scene.mode = Cesium.SceneMode.SCENE3D;
