            let autoRotate = true;
            const rotationAxis = new Cesium.Cartesian3(0, 0, 1); // Eje Z (vertical)
            let lastTime = Date.now();
            
            function startAutoRotation() {
              // Detener cualquier rotación anterior si existe
              if (window.rotationInterval) {
                clearInterval(window.rotationInterval);
              }
              
              // Velocidad de rotación en radianes por segundo
              const rotationSpeed = -0.05 * Math.PI / 180.0;
              let lastTimestamp = Date.now();
              
              // Usar requestAnimationFrame para mayor suavidad
              function rotateCamera() {
                if (!autoRotate) {
                  window.requestAnimationFrame(rotateCamera);
