                    }
                  }
                } catch (e) {
                  console.error('Error en rotación:', e);
                }
                
                // Continuar la animación
                window.requestAnimationFrame(rotateCamera);
              }
              
              // Iniciar la animación
              window.requestAnimationFrame(rotateCamera);
            }
            
            // Función para verificar periódicamente la estabilidad del globo
            function startStabilityCheck() {
              // Ejecutar cada 10 segundos
              setInterval(function() {
                try {
                  // Verificar si el globo sigue visible
                  if (!viewer.scene.globe.show) {
                    viewer.scene.globe.show = true;
                  }
                  
                  // Registrar en el debug
                  const debugDiv = document.getElementById('cesiumDebugInfo');
                  if (debugDiv) {
                    const stabilityInfo = document.createElement('div');
                    stabilityInfo.id = 'stabilityCheck';
                    stabilityInfo.style.color = '#FFA000';
                    stabilityInfo.textContent = 'Verificación de estabilidad: OK';
                    
                    // Reemplazar o agregar
                    const existingInfo = document.getElementById('stabilityCheck');
                    if (existingInfo) {
                      existingInfo.textContent = stabilityInfo.textContent;
                    } else {
                      debugDiv.appendChild(stabilityInfo);
                    }
                  }
                } catch (e) {
                  console.error('Error en verificación de estabilidad:', e);
                }
              }, 10000);
            }
            
            // Función para monitorear la posición de la cámara y enviarla al componente React Native
            function startCameraPositionMonitoring() {
              setInterval(function() {
                try {
                  if (!viewer || !viewer.scene || !viewer.camera) return;
                  
                  // Obtener la posición de la cámara en coordenadas geográficas
                  const position = viewer.camera.positionCartographic;
                  const longitude = Cesium.Math.toDegrees(position.longitude);
                  const latitude = Cesium.Math.toDegrees(position.latitude);
                  const altitude = position.height;
                  
                  // Calcular un valor aproximado de zoom basado en la altura
                  // Valores aproximados: 20000000 = zoom 1, 5000000 = zoom 4, 1000000 = zoom 8
                  const zoom = Math.max(1, Math.log(20000000 / Math.max(altitude, 10000)) * 2);
                  
                  // Crear objeto con la información de posición
                  const positionData = {
                    longitude: longitude,
                    latitude: latitude,
                    altitude: altitude,
                    zoom: zoom,
                    heading: viewer.camera.heading,
                    pitch: viewer.camera.pitch,
                    roll: viewer.camera.roll
                  };
                  
                  // Enviar al componente React Native
                  window.ReactNativeWebView.postMessage('position:' + JSON.stringify(positionData));
                  
                  // Debug
                  const debugDiv = document.getElementById('cesiumDebugInfo');
                  if (debugDiv) {
                    // Agregar información de altitud
                    const altInfo = document.createElement('div');
                    altInfo.id = 'altitudeInfo';
                    altInfo.style.color = '#4CAF50';
                    altInfo.textContent = 'Altitud: ' + Math.round(altitude/1000) + ' km';
                    
                    // Reemplazar o agregar
                    const existingInfo = document.getElementById('altitudeInfo');
                    if (existingInfo) {
                      existingInfo.textContent = altInfo.textContent;
                    } else {
                      debugDiv.appendChild(altInfo);
                    }
                  }
                } catch (e) {
                  console.error('Error al monitorear posición de cámara:', e);
                }
              }, 500); // Actualizar cada 500ms
            }
            
            // Timeout para verificar si realmente tenemos un globo visible
            setTimeout(function() {
              // Verificar si tenemos capas de imagen o si el globo está visible
              if ((viewer.imageryLayers.length === 0 || !viewer.scene.globe.show) && 
                  document.getElementById('loadingOverlay').style.display !== 'none') {
                
                console.warn('No se ha detectado un globo visible, aplicando soluciones de respaldo');
                
                // Asegurar que el globo es visible
                viewer.scene.globe.show = true;
                
                // Usar color azul y cargar textura directa
                viewer.scene.globe.baseColor = Cesium.Color.BLUE;
                loadDirectTexture();
                
                // Ocultar overlay después de intentarlo todo
                setTimeout(function() {
                  document.getElementById('loadingOverlay').style.display = 'none';
                  window.ReactNativeWebView.postMessage('loaded');
                }, 3000);
              }
            }, 10000);
            
            // Manejar interacción del usuario
            let touchStarted = false;
            
            // Eventos táctiles simplificados, sin rotación automática
            viewer.screenSpaceEventHandler.setInputAction(function() {
              touchStarted = true;
              
              // Ya no es necesario manejar rotación automática
            }, Cesium.ScreenSpaceEventType.LEFT_DOWN);
            
            viewer.screenSpaceEventHandler.setInputAction(function() {
              touchStarted = false;
              
              // Ya no es necesario reactivar rotación automática
            }, Cesium.ScreenSpaceEventType.LEFT_UP);
            
            // Botón para centrar el globo (único botón que mantenemos)
            const centerButton = document.createElement('button');
            centerButton.textContent = 'Centrar Globo';
            centerButton.style.position = 'absolute';
            centerButton.style.bottom = '20px';
            centerButton.style.right = '10px';
            centerButton.style.zIndex = '1000';
            centerButton.style.padding = '8px';
            centerButton.style.backgroundColor = '#4CAF50';
            centerButton.style.color = 'white';
            centerButton.style.border = 'none';
            centerButton.style.borderRadius = '4px';
            centerButton.style.fontWeight = 'bold';
            centerButton.onclick = function() {
              // Llamar a la función de centrado
              centerGlobe();
            };
            document.body.appendChild(centerButton);
          } catch (error) {
            console.error('Error al inicializar Cesium:', error);
            window.ReactNativeWebView.postMessage('error:' + error);
          }
        }
        
        // Timeout de seguridad para asegurar que se muestra algo
        setTimeout(function() {
          if (document.getElementById('loadingOverlay').style.display !== 'none') {
            updateProgress('Carga completa (timeout)');
            document.getElementById('loadingOverlay').style.display = 'none';
            window.ReactNativeWebView.postMessage('loaded');
          }
        }, 30000);
        
        // Manejo de errores global
        window.onerror = function(message, source, lineno, colno, error) {
          console.error('Error:', message);
          
          // Manejar errores de propiedad de solo lectura
          if (message && (
            message.includes('Cannot assign to read only property') || 
            message.toString().includes('normalize') ||
            message.toString().includes('Cartesian3')
          )) {
            console.warn('Detectado error de propiedades de solo lectura, intentando recuperar...');
            
            // Reiniciar la vista de la cámara de forma segura
            try {
              if (window.viewer) {
                window.viewer.camera.flyTo({
                  destination: Cesium.Cartesian3.fromDegrees(0.0, 0.0, 15000000.0),
                  orientation: {
                    heading: 0,
                    pitch: -Cesium.Math.PI_OVER_TWO,
                    roll: 0
                  },
                  duration: 0.5
                });
              }
            } catch(e) {
              console.error('Error al recuperar la vista:', e);
            }
            
            return true; // Evita propagar el error
          }
          
          // Solo reportar errores significativos que no sean de workers
          if (!message.includes('Worker') && !message.includes('importScripts')) {
            window.ReactNativeWebView.postMessage('error:' + message);
          }
          return true;
        };
      </script>
    </body>
    </html>
  `;

  // Manejador de mensajes desde el WebView
  const handleWebViewMessage = (event: WebViewMessageEvent) => {
    const message = event.nativeEvent.data;
    
    // Verificar si el mensaje contiene datos de posición
    if (message.startsWith('position:')) {
      try {
        const positionData = JSON.parse(message.substring(9));
        setCurrentPosition(positionData);
        
        // Cambiar vistas según la altitud
        if (positionData.altitude < MAP_VIEW_DISTANCE_THRESHOLD && !showMapView) {
          setShowMapView(true);
          
          // Detener rotación automática cuando se muestra el mapa
          if (webViewRef.current) {
            webViewRef.current.injectJavaScript(`
              (function() {
                try {
                  // Detener la rotación automática
                  autoRotate = false;
                  console.log('Rotación automática detenida al mostrar el mapa');
                  
                  // Actualizar texto de depuración
                  const debugDiv = document.getElementById('cesiumDebugInfo');
                  if (debugDiv) {
                    debugDiv.innerHTML += '<br>Rotación pausada por mapa';
                  }
                } catch(e) {
                  console.error('Error al detener rotación:', e);
                }
                return true;
              })();
            `);
          }
        } else if (positionData.altitude > GLOBE_VIEW_DISTANCE_THRESHOLD && showMapView) {
          setShowMapView(false);
          
          // Reactivar rotación automática cuando volvemos al globo
          if (webViewRef.current) {
            webViewRef.current.injectJavaScript(`
              (function() {
                try {
                  // Reactivar rotación automática
                  autoRotate = true;
                  console.log('Rotación automática reactivada al volver al globo');
                  
                  // Actualizar texto de depuración
                  const debugDiv = document.getElementById('cesiumDebugInfo');
                  if (debugDiv) {
                    debugDiv.innerHTML += '<br>Rotación reactivada';
                  }
                } catch(e) {
                  console.error('Error al reactivar rotación:', e);
                }
                return true;
              })();
            `);
          }
        }
      } catch (e) {
        console.error('Error al procesar datos de posición:', e);
      }
    } else if (message === 'loaded') {
      console.log('Cesium cargado correctamente');
      setLoading(false);
      setLoadingProgress(100);
      // Limpiar el timeout cuando se carga correctamente
      if (loadTimeoutRef.current) {
        clearTimeout(loadTimeoutRef.current);
      }
    } else if (message.startsWith('error:')) {
      console.error('Error en Cesium WebView:', message.substring(6));
      setError(message.substring(6));
      handleError(message.substring(6));
    }
  };

  return (
    <View style={[styles.container, props.style]}>
      {!useFallbackView && (
        <>
          {/* Mostrar WebView con Cesium cuando showMapView es false */}
          <WebView
            ref={webViewRef}
            style={[styles.webview, showMapView ? styles.hiddenView : null]}
            originWhitelist={['*']}
            source={{ html: htmlContent }}
            javaScriptEnabled={true}
            domStorageEnabled={true}
            allowFileAccess={true}
            allowUniversalAccessFromFileURLs={true}
            allowFileAccessFromFileURLs={true}
            cacheEnabled={true}
            javaScriptCanOpenWindowsAutomatically={true}
            scrollEnabled={false}
            bounces={false}
            showsHorizontalScrollIndicator={false}
            showsVerticalScrollIndicator={false}
            onShouldStartLoadWithRequest={() => true}
            startInLoadingState={true}
            renderLoading={() => <View />}
            onMessage={handleWebViewMessage}
            onError={(syntheticEvent) => {
              const { nativeEvent } = syntheticEvent;
              console.error('WebView error:', nativeEvent);
              handleError(`WebView error: ${nativeEvent.description}`);
            }}
            onHttpError={(syntheticEvent) => {
              const { nativeEvent } = syntheticEvent;
              console.error('WebView HTTP error:', nativeEvent);
              handleError(`WebView HTTP error: ${nativeEvent.statusCode}`);
            }}
            onContentProcessDidTerminate={() => {
              console.warn('WebView process terminated, falling back to simple globe');
              handleError('La visualización 3D se cerró inesperadamente');
            }}
          />
          
          {/* Mostrar MapView cuando showMapView es true */}
          {showMapView && (
            <View style={styles.mapContainer}>
              <MapView
                ref={mapViewRef}
                style={styles.map}
                provider={PROVIDER_GOOGLE}
                mapType="satellite" // Usar vista satélite para mostrar naturaleza
                initialRegion={{
                  latitude: currentPosition.latitude,
                  longitude: currentPosition.longitude,
                  latitudeDelta: 0.0922 * (10 / Math.max(1, currentPosition.zoom)),
                  longitudeDelta: 0.0421 * (10 / Math.max(1, currentPosition.zoom)),
                }}
                onMapReady={() => {
                  // Asegurar que el mapa se actualiza con la posición actual
                  if (mapViewRef.current) {
                    mapViewRef.current.animateToRegion({
                      latitude: currentPosition.latitude,
                      longitude: currentPosition.longitude,
                      latitudeDelta: 0.0922 * (10 / Math.max(1, currentPosition.zoom)),
                      longitudeDelta: 0.0421 * (10 / Math.max(1, currentPosition.zoom)),
                    }, 500);
                  }
                }}
                onRegionChangeComplete={(region) => {
                  // Actualizar la posición actual para mantener sincronización
                  setCurrentPosition(prevState => ({
                    ...prevState,
                    latitude: region.latitude,
                    longitude: region.longitude,
                    // Calcular un zoom aproximado desde la región
                    zoom: calculateZoomFromRegion(region)
                  }));
                  
                  // Si el usuario se aleja mucho, volver a la vista de globo
                  const zoom = calculateZoomFromRegion(region);
                  if (zoom < 5) { // Valores bajos de zoom = más alejado
                    setShowMapView(false);
                    
                    // Enviar mensaje al WebView para actualizar su vista
                    if (webViewRef.current) {
                      webViewRef.current.injectJavaScript(`
                        (function() {
                          try {
                            if (window.viewer) {
                              window.viewer.camera.flyTo({
                                destination: Cesium.Cartesian3.fromDegrees(
                                  ${region.longitude}, 
                                  ${region.latitude}, 
                                  ${GLOBE_VIEW_DISTANCE_THRESHOLD}
                                ),
                                orientation: {
                                  heading: 0.0,
                                  pitch: -Cesium.Math.PI_OVER_TWO,
                                  roll: 0.0
                                },
                                duration: 1.0
                              });
                            }
                          } catch(e) {
                            console.error('Error al actualizar la vista del globo', e);
                          }
                          return true;
                        })();
                      `);
                    }
                  }
                }}
              />
              <TouchableOpacity 
                style={styles.backToGlobeButton}
                onPress={() => {
                  setShowMapView(false);
                  
                  // Actualizar la vista del globo con la posición actual del mapa
                  if (webViewRef.current && mapViewRef.current) {
                    mapViewRef.current.getCamera().then(camera => {
                      if (camera && webViewRef.current) {
                        webViewRef.current.injectJavaScript(`
                          (function() {
                            try {
                              if (window.viewer) {
                                window.viewer.camera.flyTo({
                                  destination: Cesium.Cartesian3.fromDegrees(
                                    ${camera.center.longitude}, 
                                    ${camera.center.latitude}, 
                                    ${GLOBE_VIEW_DISTANCE_THRESHOLD}
                                  ),
                                  orientation: {
                                    heading: 0.0,
                                    pitch: -Cesium.Math.PI_OVER_TWO,
                                    roll: 0.0
                                  },
                                  duration: 1.0
                                });
                              }
                            } catch(e) {
                              console.error('Error al actualizar la vista del globo', e);
                            }
                            return true;
                          })();
                        `);
                      }
                    }).catch(err => {
                      console.error('Error al obtener la cámara de MapView:', err);
                    });
                  }
                }}
              >
                <Text style={styles.backToGlobeButtonText}>Volver al Globo</Text>
              </TouchableOpacity>
            </View>
          )}
        </>
      )}
      
      {useFallbackView && (
        <FallbackGlobeView style={props.style} />
      )}
      
      {useFallbackView && (
        <View style={styles.fallbackButtonContainer}>
          <TouchableOpacity
            style={styles.retryButton}
            onPress={retryCesium}
          >
            <Text style={styles.retryButtonText}>Intentar cargar mapa 3D</Text>
          </TouchableOpacity>
        </View>
      )}
      
      {loading && !useFallbackView && (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#4CAF50" />
          <Text style={styles.loadingText}>Cargando globo terráqueo ({loadingProgress}%)...</Text>
          <View style={styles.progressBarContainer}>
            <View style={[styles.progressBar, { width: `${loadingProgress}%` }]} />
          </View>
        </View>
      )}
      
      {error && !useFallbackView && (
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>Error: {error}</Text>
          <Text style={styles.errorSubtext}>Cambiando a modo alternativo...</Text>
        </View>
      )}
    </View>
  );
};

// Función para calcular el nivel de zoom a partir de la región
const calculateZoomFromRegion = (region: { latitudeDelta: number; longitudeDelta: number; }) => {
  // Fórmula aproximada para convertir delta a nivel de zoom
  const latDelta = region.latitudeDelta;
  const lngDelta = region.longitudeDelta;
  const zoomLat = Math.log(360 / latDelta) / Math.LN2;
  const zoomLng = Math.log(360 / lngDelta) / Math.LN2;
  return Math.min(zoomLat, zoomLng);
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#000',
    position: 'relative',
  },
  webview: {
    flex: 1,
    backgroundColor: 'transparent',
  },
  loadingContainer: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.8)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    color: '#fff',
    marginTop: 10,
    fontSize: 16,
  },
  errorContainer: {
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.9)',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  errorText: {
    color: '#ff6b6b',
    fontSize: 18,
    marginBottom: 10,
    textAlign: 'center',
  },
  errorSubtext: {
    color: '#fff',
    fontSize: 14,
    textAlign: 'center',
  },
  fallbackButtonContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    zIndex: 1000,
  },
  retryButton: {
    backgroundColor: '#4CAF50',
    padding: 15,
    borderRadius: 5,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.5,
    shadowRadius: 4,
    elevation: 5,
  },
  retryButtonText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  progressBarContainer: {
    height: 10,
    width: 250,
    backgroundColor: 'rgba(255,255,255,0.2)',
    borderRadius: 5,
    overflow: 'hidden',
    marginTop: 15,
    marginHorizontal: 20,
  },
  progressBar: {
    height: '100%',
    backgroundColor: '#4CAF50',
    borderRadius: 5,
  },
  mapContainer: {
    flex: 1,
    position: 'absolute',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
  },
  map: {
    flex: 1,
  },
  backToGlobeButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    zIndex: 1000,
    padding: 10,
    backgroundColor: '#4CAF50',
    borderRadius: 5,
  },
  backToGlobeButtonText: {
    color: '#fff',
    fontSize: 14,
    fontWeight: 'bold',
  },
  hiddenView: {
    display: 'none',
  },
});

export default GlobeView;
