import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#f5f5f5',
      padding: 20,
    },
    logoContainer: {
      alignItems: 'center',
      marginTop: 60,
      marginBottom: 40,
    },
    title: {
      fontSize: 32,
      fontWeight: 'bold',
      color: '#4CAF50',
    },
    formContainer: {
      flex: 1,
      justifyContent: 'center',
    },
    input: {
      width: '100%',
      height: 50,
      backgroundColor: 'white',
      borderRadius: 10,
      paddingHorizontal: 15,
      marginBottom: 15,
      borderWidth: 1,
      borderColor: '#ddd',
    },
    button: {
      width: '100%',
      height: 50,
      backgroundColor: '#4CAF50',
      borderRadius: 10,
      justifyContent: 'center',
      alignItems: 'center',
      marginTop: 10,
    },
    buttonText: {
      color: 'white',
      fontSize: 16,
      fontWeight: 'bold',
    },
    forgotPasswordButton: {
      marginTop: 15,
      alignItems: 'center',
    },
    forgotPasswordText: {
      color: '#4CAF50',
      fontSize: 14,
      textDecorationLine: 'underline',
    },
    link: {
      color: '#4CAF50',
      fontSize: 14,
      marginTop: 20,
      textAlign: 'center',
      textDecorationLine: 'underline',
    },
    errorText: {
      color: '#f44336',
      marginBottom: 10,
      textAlign: 'center',
    },
  });

export default styles;
