import React from 'react';
import { Platform, View, StyleSheet } from 'react-native';
import MapView, { Marker, MapViewProps, PROVIDER_GOOGLE } from 'react-native-maps';

const Map = (props: MapViewProps) => {
  if (Platform.OS === 'web') {
    return (
      <View style={{ height: '100%', width: '100%' }}>
        <MapView
          {...props}
          style={[{ height: '100%', width: '100%' }, props.style]}
        />
      </View>
    );
  }

  // En Android y iOS usar expl√≠citamente el proveedor de Google Maps
  return (
    <View style={styles.container}>
      <MapView
        provider={PROVIDER_GOOGLE}
        {...props}
        style={[styles.map, props.style]}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: '100%',
    width: '100%',
  },
  map: {
    height: '100%',
    width: '100%',
  },
});

export const MapMarker = Marker;

export default Map;
