import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, TextInput, Alert, FlatList, ActivityIndicator } from 'react-native';
import { useSelector } from 'react-redux';
import { RootState } from '../../features/store';
import { supabase } from '../../services/supabase';
import { Ionicons } from '@expo/vector-icons';

interface FriendshipRequest {
  id: string;
  senderId: string;
  receiverId: string;
  status: string;
  users: {
    username: string;
  };
}

const FriendsScreen = () => {
  const { user } = useSelector((state: RootState) => state.auth);
  const [friendshipRequests, setFriendshipRequests] = useState<FriendshipRequest[]>([]);
  const [username, setUsername] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPendingRequests();
  }, [user?.id]);

  const fetchPendingRequests = async () => {
    try {
      const { data, error } = await supabase
        .from('friendship_invitations')
        .select(`
          id, senderId, created_at, receiverId, status,
          users:senderId (username)
        `)
        .eq('receiverId', user?.id)
        .eq('status', 'Pending');

      if (error) throw error;
      setFriendshipRequests(data || []);
    } catch (error: any) {
      console.error('Error al obtener solicitudes pendientes:', error.message);
      Alert.alert('Error', 'No se pudieron cargar las solicitudes de amistad');
    }
  };

  const handleAcceptRequest = async (id: string) => {
    try {
      const { data: invitation, error: invitationError } = await supabase
        .from('friendship_invitations')
        .select('senderId, receiverId')
        .eq('id', id)
        .single();

      if (invitationError) throw invitationError;

      const { senderId, receiverId } = invitation;

      const { error: updateError } = await supabase
        .from('friendship_invitations')
        .update({ status: 'Accepted' })
        .eq('id', id);

      if (updateError) throw updateError;

      const { error: insertError } = await supabase
        .from('friends')
        .insert([{ user1Id: senderId, user2Id: receiverId }]);

      if (insertError) throw insertError;

      setFriendshipRequests((prevRequests) => prevRequests.filter(request => request.id !== id));
      Alert.alert('Éxito', 'Solicitud aceptada con éxito');
    } catch (error: any) {
      console.error('Error al aceptar la solicitud:', error.message);
      Alert.alert('Error', 'No se pudo aceptar la solicitud');
    }
  };

  const handleRejectRequest = async (id: string) => {
    try {
      const { error: updateError } = await supabase
        .from('friendship_invitations')
        .update({ status: 'Rejected' })
        .eq('id', id);

      if (updateError) throw updateError;

      setFriendshipRequests((prevRequests) => prevRequests.filter(request => request.id !== id));
      Alert.alert('Éxito', 'Solicitud rechazada con éxito');
    } catch (error: any) {
      console.error('Error al rechazar la solicitud:', error.message);
      Alert.alert('Error', 'No se pudo rechazar la solicitud');
    }
  };

  const handleSendRequest = async () => {
    if (!username.trim()) {
      Alert.alert('Error', 'Por favor ingresa un nombre de usuario');
      return;
    }

    setLoading(true);
    try {
      const { data: receiver, error: userError } = await supabase
        .from('users')
        .select('id')
        .eq('username', username)
        .single();

      if (userError) throw userError;

      const receiverId = receiver.id;

      if (user?.id === receiverId) {
        Alert.alert('Error', 'No puedes enviarte una solicitud a ti mismo');
        return;
      }

      const { data: friends, error: friendsError } = await supabase
        .from('friends')
        .select('*')
        .or(`and(user1Id.eq.${user?.id},user2Id.eq.${receiverId}),and(user1Id.eq.${receiverId},user2Id.eq.${user?.id})`)
        .single();

      if (friends) {
        Alert.alert('Error', 'Ya son amigos');
        return;
      }

      const { data: existingRequest, error: requestError } = await supabase
        .from('friendship_invitations')
        .select('*')
        .or(`and(senderId.eq.${user?.id},receiverId.eq.${receiverId}),and(senderId.eq.${receiverId},receiverId.eq.${user?.id})`)
        .eq('status', 'Pending')
        .single();

      if (existingRequest) {
        Alert.alert('Error', 'Ya existe una solicitud pendiente entre estos usuarios');
        return;
      }

      const { error } = await supabase
        .from('friendship_invitations')
        .insert([{ senderId: user?.id, receiverId }]);

      if (error) throw error;

      Alert.alert('Éxito', 'Solicitud enviada con éxito');
      setUsername('');
    } catch (error: any) {
      console.error('Error al enviar la solicitud:', error.message);
      Alert.alert('Error', 'No se pudo enviar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.sendRequestContainer}>
        <TextInput
          style={styles.input}
          placeholder="Nombre de usuario"
          value={username}
          onChangeText={setUsername}
        />
        <TouchableOpacity 
          style={[styles.sendButton, loading && styles.disabledButton]} 
          onPress={handleSendRequest}
          disabled={loading}
        >
          {loading ? (
            <ActivityIndicator color="white" />
          ) : (
            <Text style={styles.sendButtonText}>Enviar Solicitud</Text>
          )}
        </TouchableOpacity>
      </View>

      <View style={styles.requestsContainer}>
        <Text style={styles.requestsTitle}>Solicitudes Pendientes</Text>
        {friendshipRequests.length === 0 ? (
          <Text style={styles.noRequestsText}>No hay solicitudes pendientes</Text>
        ) : (
          <FlatList
            data={friendshipRequests}
            keyExtractor={(item) => item.id}
            renderItem={({ item }) => (
              <View style={styles.requestItem}>
                <Text style={styles.requestText}>
                  {item.users.username || 'Usuario desconocido'} te ha enviado una solicitud.
                </Text>
                <View style={styles.requestButtons}>
                  <TouchableOpacity 
                    style={[styles.requestButton, styles.acceptButton]} 
                    onPress={() => handleAcceptRequest(item.id)}
                  >
                    <Text style={styles.requestButtonText}>Aceptar</Text>
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={[styles.requestButton, styles.rejectButton]} 
                    onPress={() => handleRejectRequest(item.id)}
                  >
                    <Text style={styles.requestButtonText}>Rechazar</Text>
                  </TouchableOpacity>
                </View>
              </View>
            )}
          />
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#f5f5f5',
  },
  sendRequestContainer: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 15,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 2,
    elevation: 3,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  sendButton: {
    backgroundColor: '#4CAF50',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
  },
  sendButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  disabledButton: {
    opacity: 0.7,
  },
  requestsContainer: {
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 15,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 2,
    elevation: 3,
  },
  requestsTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
    color: '#333',
  },
  requestItem: {
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  requestText: {
    fontSize: 16,
    marginBottom: 10,
    color: '#333',
  },
  requestButtons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  requestButton: {
    flex: 1,
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
    marginHorizontal: 5,
  },
  acceptButton: {
    backgroundColor: '#4CAF50',
  },
  rejectButton: {
    backgroundColor: '#f44336',
  },
  requestButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  noRequestsText: {
    textAlign: 'center',
    color: '#666',
    marginVertical: 20,
  },
});

export default FriendsScreen;
