import React, { useState, useEffect } from 'react';
import { View, StyleSheet, TextInput, TouchableOpacity, Text, Dimensions, Platform, Modal, ActivityIndicator, Alert, ScrollView, FlatList } from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../features/store';
import Map from '../../components/maps';
import * as Location from 'expo-location';
import { getMissionsByCityAndDuration } from '../../services/missionService';
import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import generateMission from '../../services/missionGenerator';
import { Ionicons } from '@expo/vector-icons';
import { Calendar } from 'react-native-calendars';
import { format } from 'date-fns';
import { searchCities } from '../../services/supabase';
import GlobeView from '../../components/GlobeView';
import { supabase } from '../../services/supabase';
import { shareJourney } from '../../services/shareService';

interface City {
  id: string;
  name: string;
  latitude: number;
  longitude: number;
}

interface Friend {
  user2Id: string;
  username: string;
  points: number;
}

type RootStackParamList = {
  Missions: {
    journeyId: string;
    challenges: any[];
  };
};

type NavigationProp = NativeStackNavigationProp<RootStackParamList>;

const { width, height } = Dimensions.get('window');

const LoadingModal = ({ visible, currentStep }: { visible: boolean; currentStep: string }) => (
  <Modal
    transparent={true}
    visible={visible}
    animationType="fade"
  >
    <View style={styles.modalContainer}>
      <View style={styles.loadingCard}>
        <ActivityIndicator size="large" color="#4CAF50" />
        <Text style={styles.loadingTitle}>Generando tu aventura</Text>
        <Text style={styles.loadingStep}>{currentStep}</Text>

        {/* Indicador de progreso visual */}
        <View style={styles.progressBar}>
          <View style={[
            styles.progressFill,
            {
              width: currentStep.includes('Preparando') ? '30%' :
                currentStep.includes('Buscando') ? '60%' :
                  currentStep.includes('Creando') ? '90%' : '10%'
            }
          ]} />
        </View>
      </View>
    </View>
  </Modal>
);

const DateRangePickerMobile: React.FC<{
  startDateProp: Date | null;
  endDateProp: Date | null;
  onDatesChange: (dates: [Date | null, Date | null]) => void;
}> = ({ startDateProp, endDateProp, onDatesChange }) => {
  const [startDate, setStartDate] = useState<Date | null>(startDateProp);
  const [endDate, setEndDate] = useState<Date | null>(endDateProp);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedDates, setSelectedDates] = useState<{ [date: string]: any }>({});

  useEffect(() => {
    setStartDate(startDateProp);
    setEndDate(endDateProp);

    if (startDateProp || endDateProp) {
      updateMarkedDates(startDateProp, endDateProp);
    }
  }, [startDateProp, endDateProp]);

  const updateMarkedDates = (start: Date | null, end: Date | null) => {
    const newMarkedDates: { [date: string]: any } = {};

    if (start) {
      const startStr = format(start, 'yyyy-MM-dd');
      newMarkedDates[startStr] = {
        startingDay: true,
        color: '#005F9E',
        textColor: 'white'
      };
    }

    if (end) {
      const endStr = format(end, 'yyyy-MM-dd');
      newMarkedDates[endStr] = {
        endingDay: true,
        color: '#005F9E',
        textColor: 'white'
      };
    }

    if (start && end) {
      let currentDate = new Date(start);
      currentDate.setDate(currentDate.getDate() + 1);

      while (currentDate < end) {
        const dateStr = format(currentDate, 'yyyy-MM-dd');
        newMarkedDates[dateStr] = {
          color: '#e5f3ff',
          textColor: '#005F9E'
        };
        currentDate.setDate(currentDate.getDate() + 1);
      }
    }

    setSelectedDates(newMarkedDates);
  };

  const handleDayPress = (day: any) => {
    const selectedDate = new Date(day.dateString);

    if (!startDate || (startDate && endDate)) {
      const newStart = selectedDate;
      setStartDate(newStart);
      setEndDate(null);
      updateMarkedDates(newStart, null);
      onDatesChange([newStart, null]);
    } else {
      if (selectedDate < startDate) {
        const newEnd = new Date(startDate);
        setEndDate(newEnd);
        setStartDate(selectedDate);
        updateMarkedDates(selectedDate, newEnd);
        onDatesChange([selectedDate, newEnd]);
      } else {
        setEndDate(selectedDate);
        updateMarkedDates(startDate, selectedDate);
        onDatesChange([startDate, selectedDate]);
      }
    }
  };

  const calculateDuration = (start: Date | null, end: Date | null): number => {
    if (!start || !end) return 0;
    const diffTime = Math.abs(end.getTime() - start.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const formatDateRange = (): string => {
    if (!startDate && !endDate) {
      return 'Selecciona fechas';
    }

    const formatDate = (date: Date | null): string => {
      if (!date) return '';
      return format(date, 'dd MMM');
    };

    const duration = calculateDuration(startDate, endDate);
    const startStr = formatDate(startDate);
    const endStr = formatDate(endDate);

    if (startStr && endStr) {
      return `${startStr} - ${endStr} Â· ${duration} noches`;
    } else if (startStr) {
      return `${startStr} - Selecciona fecha final`;
    } else {
      return 'Selecciona fechas';
    }
  };

  return (
    <View style={styles.datePickerContainer}>
      <TouchableOpacity
        style={styles.datePickerButton}
        onPress={() => setIsOpen(!isOpen)}
      >
        <View style={styles.datePickerContent}>
          <Text style={styles.datePickerText}>{formatDateRange()}</Text>
          <Ionicons name={isOpen ? 'chevron-up' : 'chevron-down'} size={24} color="white" />
        </View>
      </TouchableOpacity>
      {isOpen && (
        <View style={styles.calendarDropdown}>
          <Calendar
            minDate={new Date().toISOString().split('T')[0]}
            onDayPress={handleDayPress}
            markingType="period"
            markedDates={selectedDates}
            theme={{
              calendarBackground: '#ffffff',
              textSectionTitleColor: '#b6c1cd',
              selectedDayBackgroundColor: '#005F9E',
              selectedDayTextColor: '#ffffff',
              todayTextColor: '#005F9E',
              dayTextColor: '#2d4150',
              textDisabledColor: '#d9e1e8',
              dotColor: '#005F9E',
              selectedDotColor: '#ffffff',
              arrowColor: '#005F9E',
              monthTextColor: '#005F9E',
              indicatorColor: '#005F9E',
            }}
          />
        </View>
      )}
    </View>
  );
};

// En lugar de usar react-datepicker, crear un componente propio para web
const DateRangePickerWeb: React.FC<{
  startDateProp: Date | null;
  endDateProp: Date | null;
  onDatesChange: (dates: [Date | null, Date | null]) => void;
}> = ({ startDateProp, endDateProp, onDatesChange }) => {
  const [startDate, setStartDate] = useState<Date | null>(startDateProp);
  const [endDate, setEndDate] = useState<Date | null>(endDateProp);
  const [isOpen, setIsOpen] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  useEffect(() => {
    setStartDate(startDateProp);
    setEndDate(endDateProp);
  }, [startDateProp, endDateProp]);

  // FunciÃ³n para generar el calendario
  const generateCalendar = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();

    // Obtener el primer dÃ­a del mes
    const firstDay = new Date(year, month, 1);
    const startingDayOfWeek = firstDay.getDay(); // 0 = domingo

    // Obtener el Ãºltimo dÃ­a del mes
    const lastDay = new Date(year, month + 1, 0);
    const totalDays = lastDay.getDate();

    // Crear un array para los dÃ­as del mes
    const days = [];

    // Agregar dÃ­as del mes anterior para completar la primera semana
    for (let i = 0; i < startingDayOfWeek; i++) {
      const prevMonthDay = new Date(year, month, -i);
      days.unshift({
        date: prevMonthDay,
        isCurrentMonth: false,
        isToday: false,
        isSelected: false
      });
    }

    // Agregar los dÃ­as del mes actual
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    for (let i = 1; i <= totalDays; i++) {
      const currentDate = new Date(year, month, i);
      currentDate.setHours(0, 0, 0, 0);

      const isToday = currentDate.getTime() === today.getTime();
      const isStartDate = startDate && currentDate.getTime() === new Date(startDate).setHours(0, 0, 0, 0);
      const isEndDate = endDate && currentDate.getTime() === new Date(endDate).setHours(0, 0, 0, 0);
      const isInRange = startDate && endDate &&
        currentDate.getTime() > new Date(startDate).setHours(0, 0, 0, 0) &&
        currentDate.getTime() < new Date(endDate).setHours(0, 0, 0, 0);

      days.push({
        date: currentDate,
        isCurrentMonth: true,
        isToday,
        isSelected: isStartDate || isEndDate,
        isStartDate,
        isEndDate,
        isInRange
      });
    }

    // Agregar dÃ­as del mes siguiente para completar la Ãºltima semana
    const remainingDays = 7 - (days.length % 7);
    if (remainingDays < 7) {
      for (let i = 1; i <= remainingDays; i++) {
        const nextMonthDay = new Date(year, month + 1, i);
        days.push({
          date: nextMonthDay,
          isCurrentMonth: false,
          isToday: false,
          isSelected: false
        });
      }
    }

    return days;
  };

  const handleDateClick = (date: Date) => {
    // No permitir seleccionar fechas en el pasado
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (date < today) {
      return;
    }

    if (!startDate || (startDate && endDate)) {
      // Si no hay fecha de inicio o ambas fechas estÃ¡n seleccionadas, establecer como nueva fecha de inicio
      setStartDate(date);
      setEndDate(null);
      onDatesChange([date, null]);
    } else {
      // Si solo hay fecha de inicio, establecer como fecha de fin
      if (date < startDate) {
        // Si la fecha seleccionada es anterior a la fecha de inicio, intercambiarlas
        setEndDate(new Date(startDate));
        setStartDate(date);
        onDatesChange([date, startDate]);
      } else {
        setEndDate(date);
        onDatesChange([startDate, date]);
      }
    }
  };

  const formatMonth = (date: Date) => {
    const months = [
      'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
      'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
    ];
    return `${months[date.getMonth()]} ${date.getFullYear()}`;
  };

  const changeMonth = (offset: number) => {
    const newMonth = new Date(currentMonth);
    newMonth.setMonth(newMonth.getMonth() + offset);
    setCurrentMonth(newMonth);
  };

  const days = generateCalendar(currentMonth);
  const weekDays = ['Dom', 'Lun', 'Mar', 'MiÃ©', 'Jue', 'Vie', 'SÃ¡b'];

  const calculateDuration = (start: Date | null, end: Date | null): number => {
    if (!start || !end) return 0;
    const diffTime = Math.abs(end.getTime() - start.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const formatDate = (date: Date | null): string => {
    if (!date) return '';
    return `${date.getDate()} ${['ene', 'feb', 'mar', 'abr', 'may', 'jun', 'jul', 'ago', 'sep', 'oct', 'nov', 'dic'][date.getMonth()]}`;
  };

  const formatDateRange = (): string => {
    if (!startDate && !endDate) {
      return 'Selecciona fechas';
    }

    const startStr = formatDate(startDate);
    const endStr = formatDate(endDate);
    const duration = calculateDuration(startDate, endDate);

    if (startStr && endStr) {
      return `${startStr} - ${endStr} Â· ${duration} noches`;
    } else if (startStr) {
      return `${startStr} - Selecciona fecha final`;
    } else {
      return 'Selecciona fechas';
    }
  };

  return (
    <View style={styles.datePickerContainer}>
      <TouchableOpacity
        style={styles.datePickerButton}
        onPress={() => setIsOpen(!isOpen)}
      >
        <View style={styles.datePickerContent}>
          <Text style={styles.datePickerText}>{formatDateRange()}</Text>
          <Ionicons name={isOpen ? 'chevron-up' : 'chevron-down'} size={24} color="white" />
        </View>
      </TouchableOpacity>

      {isOpen && (
        <div style={{
          position: 'absolute',
          top: '100%',
          left: 0,
          right: 0,
          backgroundColor: 'white',
          zIndex: 9999,
          boxShadow: '0px 2px 10px rgba(0,0,0,0.1)',
          borderRadius: 8,
          marginTop: 4,
          padding: 15,
          width: 620,
          marginLeft: 'auto',
          marginRight: 'auto'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            marginBottom: 10
          }}>
            <div style={{
              display: 'flex',
              flexDirection: 'row',
              alignItems: 'center'
            }}>
              <button
                onClick={() => changeMonth(-1)}
                style={{
                  background: 'none',
                  border: 'none',
                  fontSize: 24,
                  cursor: 'pointer',
                  color: '#005F9E'
                }}
              >
                &larr;
              </button>
              <h3 style={{
                margin: '0 15px',
                color: '#005F9E'
              }}>
                {formatMonth(currentMonth)}
              </h3>
              <button
                onClick={() => changeMonth(1)}
                style={{
                  background: 'none',
                  border: 'none',
                  fontSize: 24,
                  cursor: 'pointer',
                  color: '#005F9E'
                }}
              >
                &rarr;
              </button>
            </div>

            <div style={{
              display: 'flex',
              alignItems: 'center'
            }}>
              <button
                onClick={() => {
                  setStartDate(null);
                  setEndDate(null);
                  onDatesChange([null, null]);
                }}
                style={{
                  background: 'none',
                  border: '1px solid #ddd',
                  padding: '5px 10px',
                  borderRadius: 4,
                  cursor: 'pointer'
                }}
              >
                Limpiar
              </button>
            </div>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(7, 1fr)',
            gap: 5,
            textAlign: 'center'
          }}>
            {weekDays.map(day => (
              <div key={day} style={{
                padding: 5,
                fontWeight: 'bold',
                color: '#666'
              }}>
                {day}
              </div>
            ))}

            {days.map((day, index) => (
              <div
                key={index}
                onClick={() => day.isCurrentMonth && handleDateClick(day.date)}
                style={{
                  padding: 10,
                  cursor: day.isCurrentMonth ? 'pointer' : 'default',
                  backgroundColor: day.isStartDate || day.isEndDate
                    ? '#005F9E'
                    : day.isInRange
                      ? '#e5f3ff'
                      : 'transparent',
                  color: day.isStartDate || day.isEndDate
                    ? 'white'
                    : !day.isCurrentMonth
                      ? '#ccc'
                      : day.isToday
                        ? '#005F9E'
                        : '#333',
                  fontWeight: day.isToday ? 'bold' : 'normal',
                  borderRadius: 4,
                  opacity: day.isCurrentMonth ? 1 : 0.5
                }}
              >
                {day.date.getDate()}
              </div>
            ))}
          </div>
        </div>
      )}
    </View>
  );
};

const FriendSelectionModal = ({ visible, onClose, onSelect }: {
  visible: boolean;
  onClose: () => void;
  onSelect: (friend: Friend) => void;
}) => {
  const [friends, setFriends] = useState<Friend[]>([]);
  const [loading, setLoading] = useState(true);
  const user = useSelector((state: RootState) => state.auth.user);

  useEffect(() => {
    if (visible) {
      const fetchFriends = async () => {
        if (!user) {
          setLoading(false);
          return;
        }
        try {
          setLoading(true);
          const { data: friendData, error } = await supabase
            .from('friends')
            .select('user2Id')
            .eq('user1Id', user.id);
          if (error) throw error;

          const friendDetails = await Promise.all(
            friendData.map(async (friend: { user2Id: string }) => {
              const { data: userData, error: userError } = await supabase
                .from('users')
                .select('username, points')
                .eq('id', friend.user2Id)
                .single();
              if (userError) return null;
              return {
                user2Id: friend.user2Id,
                username: userData.username,
                points: userData.points,
              };
            })
          );

          setFriends(friendDetails.filter((f) => f !== null) as Friend[]);
        } catch (error) {
          console.error('Error fetching friends:', error);
        } finally {
          setLoading(false);
        }
      };
      fetchFriends();
    }
  }, [visible, user]);

  return (
    <Modal visible={visible} animationType="slide" onRequestClose={onClose} transparent>
      <View style={modalStyles.modalOverlay}>
        <View style={modalStyles.modalContent}>
          <Text style={modalStyles.modalTitle}>Â¿Quieres compartir tu aventura?</Text>
          <Text style={modalStyles.modalSubtitle}>Selecciona un amigo para compartir este viaje</Text>
          {loading ? (
            <ActivityIndicator size="large" color="#4CAF50" />
          ) : (
            <FlatList
              data={friends}
              keyExtractor={(item) => item.user2Id}
              renderItem={({ item }) => (
                <TouchableOpacity style={modalStyles.friendItem} onPress={() => onSelect(item)}>
                  <Text style={modalStyles.friendName}>{item.username}</Text>
                  <Text style={modalStyles.friendPoints}>Puntos: {item.points}</Text>
                </TouchableOpacity>
              )}
              ListEmptyComponent={
                <Text style={modalStyles.emptyText}>No tienes amigos agregados</Text>
              }
            />
          )}
          <View style={modalStyles.buttonContainer}>
            <TouchableOpacity style={modalStyles.cancelButton} onPress={onClose}>
              <Text style={modalStyles.cancelButtonText}>No compartir</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
};

