    }

    const missionCountNum = parseInt(missionCount) || 3; // Usar 3 como valor por defecto si no hay nÃºmero

    if (!duration || duration <= 0) {
      setErrorMsg('Por favor, selecciona fechas vÃ¡lidas para crear un viaje');
      return;
    }

    if (!startDate || !endDate) {
      setErrorMsg('Selecciona fechas de inicio y fin');
      return;
    }

    if (!searchCity) {
      setErrorMsg('Por favor, ingresa una ciudad de destino');
      return;
    }

    try {
      const validStartDate = new Date(startDate.getTime());
      const validEndDate = new Date(endDate.getTime());

      if (validEndDate < validStartDate) {
        setErrorMsg('La fecha de fin no puede ser anterior a la fecha de inicio');
        return;
      }

      // Iniciar el proceso de generaciÃ³n
      setIsLoading(true);
      setErrorMsg(null);

      // Paso 1: Preparando el viaje
      setCurrentStep(`Preparando tu viaje a ${searchCity.toUpperCase()}...`);
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Paso 2: Buscando lugares
      setCurrentStep(`Buscando lugares interesantes en ${searchCity.toUpperCase()}...`);
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Paso 3: Creando misiones
      setCurrentStep(`Creando ${missionCountNum} misiones emocionantes...`);

      // Llamar a la API para generar las misiones
      const result = await generateMission(
        searchCity.toUpperCase(),
        duration,
        missionCountNum,
        user.id,
        validStartDate,
        validEndDate
      );

      if (!result.journeyId) {
        throw new Error('No se recibiÃ³ el ID del journey');
      }

      // Paso final: Completado
      setCurrentStep('Â¡Aventura generada con Ã©xito!');
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Guardar el ID del journey generado
      setGeneratedJourneyId(result.journeyId);

      // Mostrar el modal de compartir
      setShowShareModal(true);

    } catch (error) {
      console.error('Error generando misiones:', error);
      setErrorMsg('Error al generar las misiones. Por favor, intenta de nuevo.');
    } finally {
      setIsLoading(false);
      setCurrentStep('');
    }
  };

  const onDatesChange = (dates: [Date | null, Date | null]) => {
    const [start, end] = dates;

    console.log('MapScreen - Fechas seleccionadas:', { start, end });

    setStartDate(start);
    setEndDate(end);

    if (start && end) {
      const diffTime = Math.abs(end.getTime() - start.getTime());
      const newDuration = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      console.log('Nueva duraciÃ³n calculada:', newDuration);
      setDuration(newDuration);
    } else {
      setDuration(0);
    }
  };

  // Elegir el componente correcto segÃºn la plataforma
  const DateRangePicker = Platform.OS === 'web' ? DateRangePickerWeb : DateRangePickerMobile;

  return (
    <View style={styles.container}>
      <View style={styles.searchContainer}>
        <TextInput
          style={styles.input}
          placeholder="Buscar ciudad"
          value={searchCity}
          onChangeText={handleCitySearch}
        />
        {showSuggestions && filteredCities.length > 0 && (
          <View style={styles.suggestionsList}>
            {filteredCities.map((city) => (
              <TouchableOpacity
                key={city.id}
                style={styles.suggestionItem}
                onPress={() => handleCitySelect(city)}
              >
                <Text style={styles.suggestionText}>{city.name}</Text>
              </TouchableOpacity>
            ))}
          </View>
        )}
        <TextInput
          style={styles.input}
          placeholder="NÃºmero de misiones"
          value={missionCount}
          onChangeText={setMissionCount}
          keyboardType="numeric"
        />

        <DateRangePicker
          startDateProp={startDate}
          endDateProp={endDate}
          onDatesChange={onDatesChange}
        />

        <Text style={styles.durationText}>
          DuraciÃ³n del viaje: {calculateDuration(startDate, endDate)} dÃ­as
        </Text>

        <TouchableOpacity
          style={[styles.button, isLoading && styles.buttonDisabled]}
          onPress={handleSearch}
          disabled={isLoading}
        >
          <Text style={styles.buttonText}>
            {isLoading ? 'Generando...' : 'Buscar Aventuras'}
          </Text>
        </TouchableOpacity>

        {errorMsg ? <Text style={styles.errorText}>{errorMsg}</Text> : null}
      </View>

      <View style={styles.mapContainer}>
        {isLoadingLocation ? (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#4CAF50" />
            <Text style={styles.loadingText}>Obteniendo ubicaciÃ³n...</Text>
            {errorLocationMsg && (
              <Text style={styles.errorText}>{errorLocationMsg}</Text>
            )}
            <TouchableOpacity
              style={styles.retryButton}
              onPress={() => {
                console.log('Reintentando obtener ubicaciÃ³n...');
                setIsLoadingLocation(true);
                setErrorLocationMsg(null);
                (async () => {
                  try {
                    const { status } = await Location.requestForegroundPermissionsAsync();
                    if (status !== 'granted') {
                      throw new Error('Permiso de ubicaciÃ³n denegado');
                    }

                    const location = await Location.getCurrentPositionAsync({
                      accuracy: Location.Accuracy.Balanced,
                    });

                    console.log('Nueva ubicaciÃ³n obtenida:', JSON.stringify(location.coords));

                    setUserLocation({
                      latitude: location.coords.latitude,
                      longitude: location.coords.longitude,
                    });

                    setRegion({
                      latitude: location.coords.latitude,
                      longitude: location.coords.longitude,
                      latitudeDelta: 0.0922,
                      longitudeDelta: 0.0421,
                    });

                    setIsLoadingLocation(false);
                  } catch (error: any) {
                    console.error('Error al reintentar obtener ubicaciÃ³n:', error);
                    setErrorLocationMsg(`Error: ${error.message}`);
                    setIsLoadingLocation(false);

                    // Usar ubicaciÃ³n por defecto
                    setUserLocation({
                      latitude: 40.416775,
                      longitude: -3.703790,
                    });
                  }
                })();
              }}
            >
              <Text style={styles.retryButtonText}>Reintentar</Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={[styles.retryButton, { marginTop: 10, backgroundColor: '#FFA000' }]}
              onPress={() => {
                console.log('Usando ubicaciÃ³n por defecto...');
                setUserLocation({
                  latitude: 40.416775, // Madrid
                  longitude: -3.703790,
                });
                setRegion({
                  latitude: 40.416775,
                  longitude: -3.703790,
                  latitudeDelta: 0.0922,
                  longitudeDelta: 0.0421,
                });
                setIsLoadingLocation(false);
              }}
            >
              <Text style={styles.retryButtonText}>Usar ubicaciÃ³n por defecto</Text>
            </TouchableOpacity>
          </View>
        ) : (
          <View style={{ flex: 1, width: '100%' }}>
            {errorLocationMsg && (
              <View style={styles.errorOverlay}>
                <Text style={styles.errorText}>{errorLocationMsg}</Text>
              </View>
            )}
            <GlobeView style={styles.map} />
          </View>
        )}
      </View>

      <LoadingModal visible={isLoading} currentStep={currentStep} />

      <FriendSelectionModal
        visible={showShareModal}
        onClose={() => {
          setShowShareModal(false);
          // Si el usuario no quiere compartir, navegar directamente a misiones
          if (generatedJourneyId) {
            navigation.navigate('Missions', {
              journeyId: generatedJourneyId,
              challenges: []
            });
            setGeneratedJourneyId(null);
          }
        }}
        onSelect={handleShareJourney}
      />
    </View>
  );
};

