import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export type AuthStateType = 'authenticated' | 'unauthenticated' | 'loading';

export interface AuthSliceState {
  token: string | null;
  user: User | null;
  authState: 'authenticated' | 'unauthenticated' | 'loading';
}
export interface User {
  id: string;
  username?: string;
  email: string;
  profilePicture?: string;
}
const initialState: AuthSliceState = {
  token: null,
  user: null,
  authState: 'loading',
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {
    setToken(state, action: PayloadAction<string>) {
      state.token = action.payload;
      state.authState = 'authenticated';
    },
    clearToken(state) {
      state.token = null;
      state.authState = 'unauthenticated';
    },
    setAuthState(state, action: PayloadAction<AuthStateType>) {
      state.authState = action.payload;
    },
  },
});

export const { setToken, clearToken, setAuthState } = authSlice.actions;
export default authSlice.reducer;
