import React, { createContext, useContext, useEffect, useState } from 'react';
import * as Notifications from 'expo-notifications';
import { registerForPushNotificationsAsync, savePushToken } from '../services/notificationsService';
import { useSelector } from 'react-redux';
import { RootState } from '../features/store';
import { checkAndScheduleExpiringMissions } from '../services/notificationTriggers';

interface NotificationsContextType {
    expoPushToken: string | null;
    notification: Notifications.Notification | null;
}

const NotificationsContext = createContext<NotificationsContextType>({
    expoPushToken: null,
    notification: null,
});

export const useNotifications = () => useContext(NotificationsContext);

export const NotificationsProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [expoPushToken, setExpoPushToken] = useState<string | null>(null);
    const [notification, setNotification] = useState<Notifications.Notification | null>(null);
    const { user } = useSelector((state: RootState) => state.auth);

    useEffect(() => {
        // Configurar el listener de notificaciones
        const notificationListener = Notifications.addNotificationReceivedListener(notification => {
            setNotification(notification);
        });

        const responseListener = Notifications.addNotificationResponseReceivedListener(response => {
            console.log('Usuario interactuó con la notificación:', response);
            // Aquí podríamos manejar la navegación basada en el tipo de notificación
            const { type, missionId } = response.notification.request.content.data;
            if (type === 'mission_reminder' || type === 'mission_expiring') {
                // Navegar a la pantalla de misiones con el ID de la misión
                // Esto requeriría inyectar la navegación en el contexto
            }
        });

        // Registrar el dispositivo para notificaciones push
        const registerForPushNotifications = async () => {
            try {
                const token = await registerForPushNotificationsAsync();
                if (token && user?.id) {
                    setExpoPushToken(token);
                    await savePushToken(user.id, token);

                    // Verificar y programar notificaciones para misiones pendientes
                    await checkAndScheduleExpiringMissions(user.id);
                }
            } catch (error) {
                console.error('Error registrando para notificaciones push:', error);
            }
        };

        registerForPushNotifications();

        return () => {
            Notifications.removeNotificationSubscription(notificationListener);
            Notifications.removeNotificationSubscription(responseListener);
        };
    }, [user?.id]);

    return (
        <NotificationsContext.Provider value={{ expoPushToken, notification }}>
            {children}
        </NotificationsContext.Provider>
    );
};
