import * as Notifications from 'expo-notifications';
import * as Device from 'expo-device';
import { Platform } from 'react-native';
import { supabase } from './supabase';
import { Notification, PushToken } from '../types/notifications';

export class NotificationService {
    static async registerForPushNotificationsAsync() {
        let token;

        if (Platform.OS === 'android') {
            await Notifications.setNotificationChannelAsync('default', {
                name: 'default',
                importance: Notifications.AndroidImportance.MAX,
                vibrationPattern: [0, 250, 250, 250],
                lightColor: '#FF231F7C',
            });
        }

        if (Device.isDevice) {
            const { status: existingStatus } = await Notifications.getPermissionsAsync();
            let finalStatus = existingStatus;

            if (existingStatus !== 'granted') {
                const { status } = await Notifications.requestPermissionsAsync();
                finalStatus = status;
            }

            if (finalStatus !== 'granted') {
                console.log('Failed to get push token for push notification!');
                return;
            }

            token = (await Notifications.getExpoPushTokenAsync()).data;

            // Guardar el token en Supabase
            const { data: { user } } = await supabase.auth.getUser();
            if (user) {
                try {
                    const { data, error } = await supabase
                        .from('user_push_tokens')
                        .upsert({
                            user_id: user.id,
                            token: token,
                            device_id: await Device.getDeviceIdAsync(),
                            platform: Platform.OS
                        }, {
                            onConflict: 'user_id,token'
                        })
                        .select()
                        .single();

                    if (error) {
                        console.error('Error saving push token:', error);
                    } else {
                        console.log('Push token saved successfully:', data);
                    }
                } catch (error) {
                    console.error('Error in savePushToken:', error);
                }
            }
        } else {
            console.log('Must use physical device for Push Notifications');
        }

        return token;
    }

    static async createNotification(userId: string, title: string, message: string, type: string, data?: any): Promise<Notification> {
        try {
            const { data: notification, error } = await supabase
                .from('notifications')
                .insert({
                    userid: userId,
                    title,
                    message,
                    type,
                    data,
                    read: false
                })
                .select()
                .single();

            if (error) throw error;
            return notification as Notification;
        } catch (error) {
            console.error('Error creating notification:', error);
            throw error;
        }
    }

    static async getUnreadNotifications(userId: string): Promise<Notification[]> {
        try {
            const { data, error } = await supabase
                .from('notifications')
                .select('*')
                .eq('userid', userId)
                .eq('read', false)
                .order('created_at', { ascending: false });

            if (error) throw error;
            return data as Notification[];
        } catch (error) {
            console.error('Error getting unread notifications:', error);
            throw error;
        }
    }

    static async markNotificationAsRead(notificationId: string): Promise<Notification> {
        try {
            const { data, error } = await supabase
                .from('notifications')
                .update({ read: true })
                .eq('id', notificationId)
                .select()
                .single();

            if (error) throw error;
            return data as Notification;
        } catch (error) {
            console.error('Error marking notification as read:', error);
            throw error;
        }
    }

    static async scheduleNotification(title: string, body: string, data: any = {}) {
        await Notifications.scheduleNotificationAsync({
            content: {
                title,
                body,
                data,
            },
            trigger: null,
        });
    }
}
