import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { CompositeScreenProps } from '@react-navigation/native';
import { BottomTabScreenProps } from '@react-navigation/bottom-tabs';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import { MainTabs, RootStackParams } from '../../navigation/AppNavigator';

type Props = CompositeScreenProps<
  BottomTabScreenProps<MainTabs, 'Home'>,
  NativeStackScreenProps<RootStackParams>
>;


const colors = {
  primary: '#005F9E',
  secondary: '#FFFFFF',
  danger: '#D32F2F',
  backgroundGradient: ['#005F9E', '#F0F0F0'],
};
  
export default function HomeScreen({ navigation }: Props) {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Men√∫ Principal</Text>
  
        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Map')}>
          <Text style={styles.cardText}>Ir al Mapa</Text>
        </TouchableOpacity>
  
        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Missions')}>
          <Text style={styles.cardText}>Ir a Misiones</Text>
        </TouchableOpacity>
  
        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Journal')}>
          <Text style={styles.cardText}>Ir al Diario</Text>
        </TouchableOpacity>
  
        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Profile')}>
          <Text style={styles.cardText}>Ir al Perfil</Text>
        </TouchableOpacity>
      </View>
    );
}
  
const styles = StyleSheet.create({
container: {
    flex: 1,
    backgroundColor: colors.backgroundGradient[1],
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
},
title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: colors.primary,
    marginBottom: 30,
},
card: {
    backgroundColor: colors.secondary,
    borderRadius: 10,
    padding: 15,
    marginBottom: 15,
    width: '100%',
    alignItems: 'center',
    elevation: 5,
},
cardText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
},
});
