import React from 'react';
import { Platform, View, StyleSheet, Text } from 'react-native';
import MapView, { Marker, MapViewProps, PROVIDER_GOOGLE } from 'react-native-maps';

const Map = (props: MapViewProps) => {
  if (Platform.OS === 'web') {
    return (
      <View style={{ height: '100%', width: '100%' }}>
        <MapView
          {...props}
          style={[{ height: '100%', width: '100%' }, props.style]}
        />
      </View>
    );
  }

  // Usamos un componente envoltorio para capturar errores
  try {
    return (
      <View style={styles.container}>
        <MapView
          provider={PROVIDER_GOOGLE}
          showsUserLocation={true}
          showsMyLocationButton={true}
          loadingEnabled={true}
          loadingIndicatorColor="#4CAF50"
          loadingBackgroundColor="rgba(255, 255, 255, 0.5)"
          {...props}
          style={[styles.map, props.style]}
        />
      </View>
    );
  } catch (error) {
    console.error('Error renderizando el mapa:', error);
    // Fallback si hay un error con el mapa
    return (
      <View style={[styles.container, styles.errorContainer]}>
        <Text style={styles.errorText}>Error al cargar el mapa. Por favor, verifica tu conexi√≥n.</Text>
      </View>
    );
  }
};

const styles = StyleSheet.create({
  container: {
    height: '100%',
    width: '100%',
  },
  map: {
    height: '100%',
    width: '100%',
  },
  errorContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
  },
  errorText: {
    color: '#f44336',
    textAlign: 'center',
    padding: 20,
  },
});

export const MapMarker = Marker;

export default Map;
