import React, { useState } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  Modal, 
  TouchableOpacity, 
  Image, 
  ActivityIndicator, 
  Platform 
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { Ionicons } from '@expo/vector-icons';
import { uploadImageToCloudinary } from '../services/cloudinaryService';

interface ImageUploadModalProps {
  visible: boolean;
  missionId: string;
  missionTitle: string;
  onClose: () => void;
  onSuccess: (imageUrl: string) => void;
}

const ImageUploadModal: React.FC<ImageUploadModalProps> = ({
  visible,
  missionId,
  missionTitle,
  onClose,
  onSuccess
}) => {
  const [image, setImage] = useState<string | null>(null);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const pickImage = async () => {
    setError(null);
    
    // Solicitar permisos
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    
    if (status !== 'granted') {
      setError('Se necesitan permisos para acceder a la galería');
      return;
    }
    
    // Abrir selector de imágenes
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 0.8,
    });
    
    if (!result.canceled && result.assets && result.assets.length > 0) {
      setImage(result.assets[0].uri);
    }
  };

  const takePhoto = async () => {
    setError(null);
    
    // Solicitar permisos
    const { status } = await ImagePicker.requestCameraPermissionsAsync();
    
    if (status !== 'granted') {
      setError('Se necesitan permisos para acceder a la cámara');
      return;
    }
    
    // Abrir cámara
    const result = await ImagePicker.launchCameraAsync({
      allowsEditing: true,
      aspect: [4, 3],
      quality: 0.8,
    });
    
    if (!result.canceled && result.assets && result.assets.length > 0) {
      setImage(result.assets[0].uri);
    }
  };

  const uploadImage = async () => {
    if (!image) {
      setError('Por favor, selecciona o toma una foto');
      return;
    }
    
    setUploading(true);
    setError(null);
    
    try {
      console.log('Iniciando subida de imagen para misión:', missionId);
      const imageUrl = await uploadImageToCloudinary(image, missionId);
      console.log('Imagen subida exitosamente, URL:', imageUrl);
      onSuccess(imageUrl);
    } catch (err: any) {
      console.error('Error al subir imagen:', err);
      
      // En desarrollo, mostrar mensaje de error más específico para depuración
      if (__DEV__) {
        setError(`Error al subir imagen: ${err.message || 'Error desconocido'}\n\nEn modo desarrollo, continuaremos con la imagen local.`);
        // Esperar 3 segundos y luego continuar con la imagen local para pruebas
        setTimeout(() => {
          console.log('Continuando con imagen local en modo desarrollo');
          onSuccess(image);
        }, 3000);
      } else {
        setError('Error al subir la imagen. Inténtalo de nuevo.');
      }
    } finally {
      setUploading(false);
    }
  };

  const resetModal = () => {
    setImage(null);
    setError(null);
    setUploading(false);
  };

  const handleClose = () => {
    resetModal();
    onClose();
  };

  return (
    <Modal
      animationType="slide"
      transparent={true}
      visible={visible}
      onRequestClose={handleClose}
    >
      <View style={styles.centeredView}>
        <View style={styles.modalView}>
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>Completa la misión</Text>
            <TouchableOpacity onPress={handleClose} style={styles.closeButton}>
              <Ionicons name="close" size={24} color="#666" />
            </TouchableOpacity>
          </View>
          
          <Text style={styles.missionTitle}>{missionTitle}</Text>
          
          <Text style={styles.instructions}>
            Sube una foto para completar esta misión
          </Text>
          
          {image ? (
            <View style={styles.imageContainer}>
              <Image source={{ uri: image }} style={styles.previewImage} />
              <TouchableOpacity 
                style={styles.changeImageButton}
                onPress={() => setImage(null)}
              >
                <Text style={styles.changeImageText}>Cambiar foto</Text>
              </TouchableOpacity>
            </View>
          ) : (
            <View style={styles.photoOptions}>
              <TouchableOpacity 
                style={styles.photoOptionButton}
                onPress={takePhoto}
              >
                <Ionicons name="camera" size={36} color="#005F9E" />
                <Text style={styles.photoOptionText}>Tomar foto</Text>
              </TouchableOpacity>
              
              <TouchableOpacity 
                style={styles.photoOptionButton}
                onPress={pickImage}
              >
                <Ionicons name="images" size={36} color="#005F9E" />
                <Text style={styles.photoOptionText}>Galería</Text>
              </TouchableOpacity>
            </View>
          )}
          
          {error && <Text style={styles.errorText}>{error}</Text>}
          
          <TouchableOpacity
            style={[
              styles.uploadButton,
              (!image || uploading) && styles.disabledButton
            ]}
            onPress={uploadImage}
            disabled={!image || uploading}
          >
            {uploading ? (
              <ActivityIndicator color="white" size="small" />
            ) : (
              <Text style={styles.uploadButtonText}>
                Completar misión
              </Text>
            )}
          </TouchableOpacity>
        </View>
      </View>
    </Modal>
  );
};

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalView: {
    width: '90%',
    maxWidth: 400,
    backgroundColor: 'white',
    borderRadius: 15,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  modalHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 15,
  },
  modalTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333',
  },
  closeButton: {
    padding: 5,
  },
  missionTitle: {
    fontSize: 18,
    color: '#005F9E',
    marginBottom: 15,
    fontWeight: '500',
  },
  instructions: {
    fontSize: 16,
    color: '#666',
    marginBottom: 20,
    textAlign: 'center',
  },
  photoOptions: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginBottom: 20,
  },
  photoOptionButton: {
    alignItems: 'center',
    padding: 15,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 10,
    width: 120,
  },
  photoOptionText: {
    marginTop: 8,
    color: '#333',
    fontSize: 14,
  },
  imageContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  previewImage: {
    width: '100%',
    height: 200,
    borderRadius: 10,
    marginBottom: 10,
  },
  changeImageButton: {
    padding: 8,
  },
  changeImageText: {
    color: '#005F9E',
    textDecorationLine: 'underline',
  },
  uploadButton: {
    backgroundColor: '#4CAF50',
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  disabledButton: {
    backgroundColor: '#cccccc',
  },
  uploadButtonText: {
    color: 'white',
    fontWeight: 'bold',
    fontSize: 16,
  },
  errorText: {
    color: '#f44336',
    marginBottom: 15,
    textAlign: 'center',
  },
});

export default ImageUploadModal;
