import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, Dimensions, Platform, ActivityIndicator, Modal, FlatList } from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../../features/store';
import Map, { MapMarker } from '../../components/maps/index';
import * as Location from 'expo-location';
import { getMissionsByCityAndDuration } from '../../services/missionService';
import { useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import generateMission from '../../services/missionGenerator';
import { Ionicons } from '@expo/vector-icons';
import { Calendar } from 'react-native-calendars';
import { format } from 'date-fns';
import { searchCities } from '../../services/supabase';

interface City {
  id: string;
  name: string;
  latitude: number;
  longitude: number;
}

type RootStackParamList = {
  Missions: {
    journeyId: string;
    challenges: any[];
  };
};

type NavigationProp = NativeStackNavigationProp<RootStackParamList>;

const { width, height } = Dimensions.get('window');

const LoadingModal = ({ visible, currentStep }: { visible: boolean; currentStep: string }) => (
  <Modal
    transparent={true}
    visible={visible}
    animationType="fade"
  >
    <View style={styles.modalContainer}>
      <View style={styles.loadingCard}>
        <ActivityIndicator size="large" color="#4CAF50" />
        <Text style={styles.loadingTitle}>Generando tu aventura</Text>
        <Text style={styles.loadingStep}>{currentStep}</Text>
      </View>
    </View>
  </Modal>
);

const DateRangePickerMobile: React.FC<{
  startDateProp: Date | null;
  endDateProp: Date | null;
  onDatesChange: (dates: [Date | null, Date | null]) => void;
}> = ({ startDateProp, endDateProp, onDatesChange }) => {
  const [startDate, setStartDate] = useState<Date | null>(startDateProp);
  const [endDate, setEndDate] = useState<Date | null>(endDateProp);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedDates, setSelectedDates] = useState<{[date: string]: any}>({});

  useEffect(() => {
    setStartDate(startDateProp);
    setEndDate(endDateProp);

    if (startDateProp || endDateProp) {
      updateMarkedDates(startDateProp, endDateProp);
    }
  }, [startDateProp, endDateProp]);

  const updateMarkedDates = (start: Date | null, end: Date | null) => {
    const newMarkedDates: {[date: string]: any} = {};

    if (start) {
      const startStr = format(start, 'yyyy-MM-dd');
      newMarkedDates[startStr] = { 
        startingDay: true, 
        color: '#005F9E',
        textColor: 'white'
      };
    }

    if (end) {
      const endStr = format(end, 'yyyy-MM-dd');
      newMarkedDates[endStr] = { 
        endingDay: true, 
        color: '#005F9E',
        textColor: 'white'
      };
    }

    if (start && end) {
      let currentDate = new Date(start);
      currentDate.setDate(currentDate.getDate() + 1);

      while (currentDate < end) {
        const dateStr = format(currentDate, 'yyyy-MM-dd');
        newMarkedDates[dateStr] = { 
          color: '#e5f3ff',
          textColor: '#005F9E'
        };
        currentDate.setDate(currentDate.getDate() + 1);
      }
    }

    setSelectedDates(newMarkedDates);
  };

  const handleDayPress = (day: any) => {
    const selectedDate = new Date(day.dateString);

    if (!startDate || (startDate && endDate)) {
      const newStart = selectedDate;
      setStartDate(newStart);
      setEndDate(null);
      updateMarkedDates(newStart, null);
      onDatesChange([newStart, null]);
    } else {
      if (selectedDate < startDate) {
        const newEnd = new Date(startDate);
        setEndDate(newEnd);
        setStartDate(selectedDate);
        updateMarkedDates(selectedDate, newEnd);
        onDatesChange([selectedDate, newEnd]);
      } else {
        setEndDate(selectedDate);
        updateMarkedDates(startDate, selectedDate);
        onDatesChange([startDate, selectedDate]);
      }
    }
  };

  const calculateDuration = (start: Date | null, end: Date | null): number => {
    if (!start || !end) return 0;
    const diffTime = Math.abs(end.getTime() - start.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const formatDateRange = (): string => {
    if (!startDate && !endDate) {
      return 'Selecciona fechas';
    }

    const formatDate = (date: Date | null): string => {
      if (!date) return '';
      return format(date, 'dd MMM');
    };

    const duration = calculateDuration(startDate, endDate);
    const startStr = formatDate(startDate);
    const endStr = formatDate(endDate);

    if (startStr && endStr) {
      return `${startStr} - ${endStr} Â· ${duration} noches`;
    } else if (startStr) {
      return `${startStr} - Selecciona fecha final`;
    } else {
      return 'Selecciona fechas';
    }
  };

  return (
    <View style={styles.datePickerContainer}>
      <TouchableOpacity
        style={styles.datePickerButton}
        onPress={() => setIsOpen(!isOpen)}
      >
        <View style={styles.datePickerContent}>
          <Text style={styles.datePickerText}>{formatDateRange()}</Text>
          <Ionicons name={isOpen ? 'chevron-up' : 'chevron-down'} size={24} color="white" />
        </View>
      </TouchableOpacity>
      {isOpen && (
        <View style={styles.calendarDropdown}>
          <Calendar
            minDate={new Date().toISOString().split('T')[0]}
            onDayPress={handleDayPress}
            markingType="period"
            markedDates={selectedDates}
            theme={{
              calendarBackground: '#ffffff',
              textSectionTitleColor: '#b6c1cd',
              selectedDayBackgroundColor: '#005F9E',
              selectedDayTextColor: '#ffffff',
              todayTextColor: '#005F9E',
              dayTextColor: '#2d4150',
              textDisabledColor: '#d9e1e8',
              dotColor: '#005F9E',
              selectedDotColor: '#ffffff',
              arrowColor: '#005F9E',
              monthTextColor: '#005F9E',
              indicatorColor: '#005F9E',
            }}
          />
        </View>
      )}
    </View>
  );
};

// Importar DatePicker solo para web
let DatePicker: any = null;
if (Platform.OS === 'web') {
  // ImportaciÃ³n dinÃ¡mica para evitar errores en Android
  try {
    DatePicker = require('react-datepicker').default;
    // No importes el CSS aquÃ­, lo haremos en un componente separado
  } catch (e) {
    console.warn('Error al importar react-datepicker:', e);
  }
}

// Componente para Web
const DateRangePickerWeb: React.FC<{
  startDateProp: Date | null;
  endDateProp: Date | null;
  onDatesChange: (dates: [Date | null, Date | null]) => void;
}> = ({ startDateProp, endDateProp, onDatesChange }) => {
  const [startDate, setStartDate] = useState<Date | null>(startDateProp);
  const [endDate, setEndDate] = useState<Date | null>(endDateProp);

  // Solo cargar CSS en la plataforma web
  useEffect(() => {
    if (Platform.OS === 'web') {
      // Cargar CSS dinÃ¡micamente
      const linkElement = document.createElement('link');
      linkElement.rel = 'stylesheet';
      linkElement.href = 'https://cdn.jsdelivr.net/npm/react-datepicker@4.8.0/dist/react-datepicker.css';
      document.head.appendChild(linkElement);
    }
  }, []);

  useEffect(() => {
    setStartDate(startDateProp);
    setEndDate(endDateProp);
  }, [startDateProp, endDateProp]);

  const handleDatesChange = (dates: [Date | null, Date | null]) => {
    const [start, end] = dates.map(date => date ? new Date(date) : null);

    console.log('DateRangePickerWeb - Fechas seleccionadas:', { start, end });

    setStartDate(start);
    setEndDate(end);

    if (typeof onDatesChange === 'function') {
      onDatesChange(dates);
    }
  };

  const calculateDuration = (start: Date | null, end: Date | null): number => {
    if (!start || !end) return 0;
    const diffTime = Math.abs(end.getTime() - start.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const formatDateRange = (): string => {
    if (!startDate && !endDate) {
      return 'Selecciona fechas';
    }

    const formatDate = (date: Date | null): string => {
      if (!date) return '';
      return format(date, 'dd MMM');
    };

    const duration = calculateDuration(startDate, endDate);
    const startStr = formatDate(startDate);
    const endStr = formatDate(endDate);

    if (startStr && endStr) {
      return `${startStr} - ${endStr} Â· ${duration} noches`;
    } else if (startStr) {
      return `${startStr} - Selecciona fecha final`;
    } else {
      return 'Selecciona fechas';
    }
  };

  // Asegurarse de que DatePicker estÃ¡ disponible antes de usarlo
  if (!DatePicker) {
    return (
      <View style={styles.datePickerContainer}>
        <Text>Error: DatePicker no disponible</Text>
      </View>
    );
  }

  return (
    <View style={styles.datePickerContainer}>
      <TouchableOpacity
        style={styles.datePickerButton}
      >
        <View style={styles.datePickerContent}>
          <Text style={styles.datePickerText}>{formatDateRange()}</Text>
        </View>
      </TouchableOpacity>
      <div className="booking-calendar" style={{marginTop: 10}}>
        <DatePicker
          selected={startDate}
          onChange={(date: Date, event: any) => {
            if (Array.isArray(date)) {
              handleDatesChange(date);
            } else {
              // Si solo se selecciona una fecha, mantener la fecha de inicio/fin segÃºn corresponda
              if (!startDate || (startDate && endDate)) {
                handleDatesChange([date, null]);
              } else {
                handleDatesChange([startDate, date]);
              }
            }
          }}
          startDate={startDate}
          endDate={endDate}
          selectsRange
          inline
          monthsShown={2}
          minDate={new Date()}
          dateFormat="dd/MM/yyyy"
        />
      </div>
    </View>
  );
};

const MapScreen = () => {
  const navigation = useNavigation<NavigationProp>();
  const dispatch = useDispatch();
  const { user } = useSelector((state: RootState) => state.auth);
  const [region, setRegion] = useState({
    latitude: 38.3452,
    longitude: -0.4815,
    latitudeDelta: 0.0922,
    longitudeDelta: 0.0421,
  });
  const [userLocation, setUserLocation] = useState<{ latitude: number; longitude: number } | null>(null);
  const [searchCity, setSearchCity] = useState('');
  const [missionCount, setMissionCount] = useState('');
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);
  const [showStartDatePicker, setShowStartDatePicker] = useState(false);
  const [showEndDatePicker, setShowEndDatePicker] = useState(false);
  const [errorMsg, setErrorMsg] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [currentStep, setCurrentStep] = useState('');
  const [hasLocationPermission, setHasLocationPermission] = useState(false);
  const [isLoadingLocation, setIsLoadingLocation] = useState(true);
  const [filteredCities, setFilteredCities] = useState<City[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [duration, setDuration] = useState<number>(0);

  useEffect(() => {
    const getLocation = async () => {
      try {
        let { status } = await Location.requestForegroundPermissionsAsync();

        console.log('Estado de permisos de ubicaciÃ³n:', status);

        if (status !== 'granted') {
          console.warn('Permiso de ubicaciÃ³n denegado');
          return;
        }

        console.log('Obteniendo ubicaciÃ³n actual...');
        let location = await Location.getCurrentPositionAsync({
          accuracy: Location.Accuracy.Balanced,
        });

        console.log('UbicaciÃ³n obtenida:', location);

        setUserLocation({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
        });
      } catch (error) {
        console.error('Error al obtener la ubicaciÃ³n:', error);
      }
    };

    getLocation();
  }, []);

  const handleCitySearch = async (text: string) => {
    const upperText = text.toUpperCase();
    setSearchCity(upperText);

    if (text.length > 2) {
      try {
        const cities = await searchCities(upperText);
        setFilteredCities(cities);
        setShowSuggestions(true);
      } catch (error) {
        console.error('Error searching cities:', error);
        setFilteredCities([]);
      }
    } else {
      setFilteredCities([]);
      setShowSuggestions(false);
    }
  };

  const handleCitySelect = (city: any) => {
    setSearchCity(city.name.toUpperCase());
    setShowSuggestions(false);
    setRegion({
      latitude: city.latitude,
      longitude: city.longitude,
      latitudeDelta: 0.0922,
      longitudeDelta: 0.0421,
    });
  };

  const handleSearch = async () => {
    if (!user?.id) {
      setErrorMsg('Debes iniciar sesiÃ³n para generar misiones');
      return;
    }

    const missionCountNum = parseInt(missionCount);

    if (!duration || duration <= 0) {
      setErrorMsg('Por favor, selecciona fechas vÃ¡lidas para crear un viaje');
      return;
    }

    if (!startDate || !endDate) {
      setErrorMsg('Selecciona fechas de inicio y fin');
      return;
    }

    try {
      const validStartDate = new Date(startDate.getTime());
      const validEndDate = new Date(endDate.getTime());

      if (validEndDate < validStartDate) {
        setErrorMsg('La fecha de fin no puede ser anterior a la fecha de inicio');
        return;
      }

      setIsLoading(true);
      setErrorMsg(null);

      setCurrentStep('Preparando tu viaje a ' + searchCity.toUpperCase() + '...');
      await new Promise(resolve => setTimeout(resolve, 1000));

      setCurrentStep('Buscando lugares interesantes...');
      await new Promise(resolve => setTimeout(resolve, 1500));

      setCurrentStep('Creando misiones emocionantes...');
      const result = await generateMission(
        searchCity.toUpperCase(), 
        duration, 
        missionCountNum, 
        user.id, 
        validStartDate, 
        validEndDate
      );

      if (!result.journeyId) {
        throw new Error('No se recibiÃ³ el ID del journey');
      }

      setCurrentStep('Â¡Todo listo para tu aventura!');
      await new Promise(resolve => setTimeout(resolve, 1000));

      navigation.navigate('Missions', {
        journeyId: result.journeyId,
        challenges: result.challenges || []
      });
    } catch (error) {
      console.error('Error generando misiones:', error);
      setErrorMsg('Error al generar las misiones. Por favor, intenta de nuevo.');
    } finally {
      setIsLoading(false);
      setCurrentStep('');
    }
  };

  const onDatesChange = (dates: [Date | null, Date | null]) => {
    const [start, end] = dates;

    console.log('MapScreen - Fechas seleccionadas:', { start, end });

    setStartDate(start);
    setEndDate(end);

    if (start && end) {
      const diffTime = Math.abs(end.getTime() - start.getTime());
      const newDuration = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      console.log('Nueva duraciÃ³n calculada:', newDuration);
      setDuration(newDuration);
    } else {
      setDuration(0);
    }
  };

  // Elegir el picker adecuado segÃºn la plataforma
  const DateRangePicker = Platform.OS === 'web' ? DateRangePickerWeb : DateRangePickerMobile;

  return (
    <View style={styles.container}>
      <LoadingModal visible={isLoading} currentStep={currentStep} />

      <View style={styles.searchContainer}>
        <View style={styles.searchInputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Buscar ciudad"
            value={searchCity}
            onChangeText={handleCitySearch}
            onFocus={() => setShowSuggestions(true)}
          />
          {showSuggestions && filteredCities.length > 0 && (
            <FlatList
              data={filteredCities}
              keyExtractor={(item) => item.id}
              renderItem={({ item }) => (
                <TouchableOpacity
                  style={styles.suggestionItem}
                  onPress={() => handleCitySelect(item)}
                >
                  <Text style={styles.suggestionText}>{item.name}</Text>
                </TouchableOpacity>
              )}
              style={styles.suggestionsList}
            />
          )}
        </View>
        <TextInput
          style={styles.input}
          placeholder="NÃºmero de misiones"
          value={missionCount}
          onChangeText={setMissionCount}
          keyboardType="numeric"
        />

        <DateRangePicker
          startDateProp={startDate}
          endDateProp={endDate}
          onDatesChange={onDatesChange}
        />

        <Text style={styles.durationText}>
          DuraciÃ³n: {duration} dÃ­as
        </Text>

        <TouchableOpacity
          style={[styles.button, isLoading && styles.buttonDisabled]}
          onPress={handleSearch}
          disabled={isLoading}
        >
          <Text style={styles.buttonText}>
            {isLoading ? 'Generando...' : 'Buscar Aventuras'}
          </Text>
        </TouchableOpacity>
        {errorMsg ? <Text style={styles.errorText}>{errorMsg}</Text> : null}
      </View>

      <View style={styles.mapContainer}>
        {isLoadingLocation ? (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color="#4CAF50" />
            <Text style={styles.loadingText}>Obteniendo ubicaciÃ³n...</Text>
            {errorMsg ? (
              <TouchableOpacity style={styles.retryButton} onPress={() => {
                setIsLoadingLocation(true);
                setUserLocation(null);
              }}>
                <Text style={styles.retryButtonText}>Reintentar</Text>
              </TouchableOpacity>
            ) : null}
          </View>
        ) : (
          <Map
            region={{
              latitude: userLocation?.latitude || 40.416775,
              longitude: userLocation?.longitude || -3.703790,
              latitudeDelta: 0.0922,
              longitudeDelta: 0.0421,
            }}
            style={styles.map}
            showsUserLocation={true}
            userLocation={userLocation}
          />
        )}
        {errorMsg ? (
          <View style={styles.errorOverlay}>
            <Text style={styles.errorText}>{errorMsg}</Text>
          </View>
        ) : null}
      </View>

      {/* Para depuraciÃ³n, puedes agregar esto: */}
      {__DEV__ && (
        <View style={{ 
          padding: 10, 
